[69582] = {name = "Чумная бомба", description = "[q]Жизнь заклинателя приносится в жертву для нанесения 9375 to 10625 ед. урона от огня ближайшим противникам."},
[69583] = {name = "Огненный шар", description = "[q]Наносит цели 6198 to 7202 ед. урона от огня."},
