[69586] = {name = "Адское пламя", description = "[q]Поджигает землю под ногами заклинателя, нанося 2000 ед. урона от огня заклинателю и 2000 ед. урона от огня – его противникам раз в 1 сек. Время действия заклинания – 15 sec."},
[69590] = {name = "Force Player to Summon Andorel", description = nil},
