[69603] = {name = "Гниль", description = "[q]Гнусное колдовство оскверняет цель, нанося ей урон от сил природы раз в 3 сек. Если не снять эффект за 21 sec., он перейдет на следующую цель."},
[69633] = {name = "Пелена Тени", description = "[q]Снижает эффективность получаемого исцеления на 75% на 15 sec."},
[69634] = {name = "Вкус крови", description = "[q]Вкус собственной крови придает воину сил, снижая весь получаемый им урон на 50% и восстанавливая ему 5% от максимального запаса здоровья раз в 2 сек. в течение 10 sec."},
[69638] = {name = "Боевое неистовство", description = "[q]Повышает весь урон на 12%."},
