[69649] = {name = "Ледяное дыхание", description = "[q]Наносит 55500 to 64500 ед. урона от магии льда всем противникам в конусе 60 м перед заклинателем. Кроме того, скорость атаки цели снижается на 50%, а скорость передвижения – на 15% на 1.50 min."},
[69651] = {name = "Ранящий удар", description = "[q]Нанесение урона, равного 200% урона оружия и создание раны, снижающей эффективность любых исцеляющих эффектов на 40% на 10 sec."},
[69652] = {name = "Вихрь клинков", description = "[q]Воин начинает кружиться, вихрем налетая на всех ближайших противников и нанося им удары раз в 1 сек. в течение следующих 6 sec. Находясь под действием эффекта \"Вихрь клинков\", он может передвигаться, но не может использовать любые другие способности. В этом состоянии воин не чувствует ни страха, ни жалости, и только смерть способна остановить его."},
[69674] = {name = "Мутировавшая инфекция", description = "[q]Мутировавшая инфекция наносит 4875 to 5125 ед. урона раз в 1 сек. и снижает эффективность получаемого лечения на 75% в течение 12 sec. Если вылечить инфекцию, на месте цели появляется маленький слизнюк."},
[69679] = {name = "Ракетное орудие", description = "[q]Запускает ракету, которая взрывается при приземлении, нанося урон от огня ближайшим противникам и осадный урон вражескому боевому кораблю."},
[69705] = {name = "Заморозка", description = "[q]Замораживает цель в ледяной глыбе, образованной из влаги, сконденсированной в воздухе. Заклинание необходимо поддерживать."},
[69708] = {name = "Ледяная тюрьма", description = "[q]Оглушает цель, делая ее неуязвимой. Цель не может производить какие-либо действия."},
[69745] = {name = "General Trigger 2 to Vehicle", description = nil},
[69750] = {name = "Слабый лучащийся слизнюк", description = "[q]Лучащийся слизнюк наносит 4875 to 5125 ед. урона противникам в радиусе 10 и заставляет малых слизнюков сливаться с другими находящимися рядом слизнюками."},
[69760] = {name = "Лучащийся слизнюк", description = "[q]Лучащийся слизнюк наносит 6338 to 6662 ед. урона противникам в радиусе 10 м, а также заставляет больших слизнюков сливаться с другими находящимися рядом слизнюками."},
[69762] = {name = "Освобожденная магия", description = "[q]Насылает на цель тайное проклятие. Каждое заклинание цели будет вызывать ответный удар тайной магией через 5 sec. после произнесения. Чем больше заклинаний применит цель, тем больше урона она получит."},
[69774] = {name = "Липкая жижа", description = "[q]Слизнюк расплескивает брызги зеленой гадости рядом с целью. Зеленая гадость наносит 3900 to 4100 ед. урона раз в 1 сек. и снижает скорость передвижения на 50%."},
[69776] = {name = "Липкая жижа", description = "[q]Зеленая гадость наносит 3900 to 4100 ед. урона раз в 1 сек. и снижает скорость передвижения на 50%."},
[69780] = {name = "Беспощадность зимы", description = "[q]Уменьшает скорость передвижения на 75%."},
[69787] = {name = "Ледяная преграда", description = "[q]Заклинателя мгновенно окружает защитный барьер, поглощающий 100000000 ед. урона. Действует until cancelled. Пока щит держится, заклинания невозможно прервать."},
[69808] = {name = "Боевой штандарт \"Усмирителя небес\"", description = "[q]Увеличивает максимальный запас здоровья на 15% и повышает скорость атак ближнего и дальнего боя и произнесения заклинаний на 25% у всех дружественных целей в радиусе 80 на 2 min."},
[69809] = {name = "Кор'кронский боевой штандарт", description = "[q]Увеличивает максимальный запас здоровья на 15% и повышает скорость атак ближнего и дальнего боя и произнесения заклинаний на 25% у всех дружественных целей в радиусе 80 на 2 min."},
[69810] = {name = "Призыв боевого штандарта \"Усмирителя небес\"", description = "[q]Призыв 0 суккубов на помощь заклинателю."},
