[70189] = {name = "Ядовитый плевок", description = "[q]Наносит 5370 to 6630 ед. урона от сил природы, а затем 1388 to 1612 ед. урона раз в 2 сек. в течение 8 sec. Эффект также действует на цели в радиусе 5 м."},
[70191] = {name = "Рассекающий удар", description = "[q]Наносит стандартный урон противнику и его ближайшим союзникам. Поражает до 10 целей."},
[70194] = {name = "Темные путы", description = "[q]Оглушает цель, делая ее неуязвимой. Цель не может производить какие-либо действия."},
[70196] = {name = "Пронзающее копье", description = "[q]Проткнуть дальнего противника оружием, мгновенно убив его."},
[70215] = {name = "Газовое вздутие", description = "[q]Игрок, находящийся под действием эффекта \"Газовое вздутие\", притягивает к себе газовое облако. При столкновении игрок начинает испускать газ, нанося урон в зависимости от степени вздутия."},
[70218] = {name = "Сеть для вепря", description = "[q]Обездвиживает противника на 15 sec."},
[70274] = {name = "Токсичный выброс", description = "[q]Наносит урон от сил природы всем противникам, погруженным в кислоту. Время действия – 10 sec."},
[70278] = {name = "Колотая рана", description = "[q]Наносит противнику 2388 to 2612 ед. физического урона раз в 1 сек. в течение 5 sec."},
[70291] = {name = "Ледяной клинок", description = "[q]Ваше оружие наполняется мощью Ледяной Короны и наносит урон от магии льда в течение 14 sec."},
[70292] = {name = "Леденящий удар", description = "[q]Жестокая атака, после которой цель начинает кровоточить, получая 15% урона от магии льда раз в 3 сек. Эффект снимается только при полном исцелении цели."},
[70299] = {name = "Вытягивание сущности", description = nil},
[70302] = {name = "Бросок грязи в глаза", description = "[q]Ослепление цели. Цель растерянно бродит туда-сюда в течение 3 sec. Любой нанесенный урон снимает эффект."},
[70304] = {name = "Кровь могучего героя", description = "[q]Увеличивает наносимый урон на 50%.\n Повышает скорость атаки в ближнем и дальнем бою на 100%.\n Заклинания и способности применяются мгновенно и без затрат ресурсов."},
[70306] = {name = "Ледяной клинок", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому двойной урон. Время действия – 6 sec."},
[70307] = {name = "Summon 4 Alliance", description = nil},
[70309] = {name = "Ранящий бросок", description = "[q]Бросок оружия, наносящий противнику 3010 to 3990 ед. урона и дополнительно 2000 ед. физического урона раз в 3 сек. в течение 18 sec."},
[70316] = {name = "Рассекающий удар", description = "[q]Нанесение в ближнем бою 110% обычного урона противнику и его ближайшим союзникам. Захватывает до 3 целей."},
[70337] = {name = "Мертвящая чума", description = "[q]Заражает противника, нанося 150000 ед. урона от темной магии раз в 5 сек. в течение 15 sec. Если противник умирает, находясь под действием чумы, или действие эффекта заканчивается, эффект получает дополнительный заряд и переходит на соседнюю цель. При попытке рассеивания снимается один заряд эффекта, при этом чума также переходит на соседнюю цель. Сила Короля-лича увеличивается при каждом переходе мертвящей чумы на новую цель."},
[70340] = {name = "Львиная ярость", description = "[q]Мощь льва увеличивает силу вашей атаки."},
[70341] = {name = "Лужа слизи", description = "[q]Бросок флакона мутировавшей слизи. После приземления флакона образуется лужа слизи."},
[70343] = {name = "Лужа слизи", description = "[q]Мутировавшая слизь не переставая растет, нанося 6825 to 7175 ед. урона раз в 1 с, пока ее не поглотит другое мутировавшее создание!"},
[70351] = {name = "Непредсказуемый эксперимент", description = "[q]Профессор Мерзоцид начинает проводить над всеми вами зловещий эксперимент!"},
[70358] = {name = "Призыв вурдалаков", description = "[q]Призыв 3 вурдалаков-приспешников в течение 3 sec."},
[70360] = {name = "Съесть слизнюка", description = "[q]Съесть мутировавшую слизь, уменьшив размер лужи и получив 4 ед. энергии слизнюка!"},
[70361] = {name = "Рассекающий удар", description = "[q]Наносит 120% урона от оружия противнику и его ближайшим союзникам. Действует максимум на 3 целей."},
[70362] = {name = "Снежная буря", description = "[q]Начинается снежная буря, длящаяся 8 sec., наносящая 8000 ед. урона от магии льда раз в 2 сек. и снижающая скорость передвижения противников в зоне поражения на 30%."},
[70363] = {name = "Каннибализм", description = "[q]Восполнение здоровья в течение 12 sec."},
[70371] = {name = "Исступление", description = "[q]Снижает время между атаками на 100% на 20 sec."},
[70372] = {name = "Призыв шаркающего ужаса", description = "[q]Призывает шаркающего ужаса."},
[70381] = {name = "Глубокая заморозка", description = "[q]Замораживает случайную цель, снижая скорость ее передвижения на 50% и нанося значительный урон от магии льда через 14 сек."},
[70386] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[70388] = {name = "Семя порчи", description = "[q]Внедрение в противника демонического семени, которое наносит 1500 ед. урона от темной магии в течение 18 sec. При нанесении 2500 ед. урона, семя взрывается, нанося урон от темной магии всем противникам в радиусе 10 м от цели."},
[70391] = {name = "Проклятие агонии", description = "[q]Цель бьется в агонии, получая 7200 ед. урона от темной магии в течение 24 sec. Урон увеличивается постепенно и достигает своего пика к концу действия проклятия. Чернокнижник может наложить на каждую цель только одно проклятие."},
[70392] = {name = "Черное клеймо", description = "[q]Наносит противнику 139 to 161 ед. урона от темной магии и увеличивает получаемый им урона от темной магии на 50% на 10 sec."},
[70394] = {name = "Болезнетворное облако", description = "[q]Гнилостное облако заразы окружает заклинателя, нанося урон от сил природы находящимся поблизости противникам."},
[70396] = {name = "Призрачный выпад", description = "[q]Усиленная атака, которая наносит дополнительно 3938 to 5062 единиц физического урона."},
[70404] = {name = "Ленточка Прелести", description = "[q]Почетная лента."},
[70405] = {name = "Мутация", description = "[q]Болезни, распространяемые мутировавшим поганищем, наносят урон находящимся рядом игрокам."},
[70407] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 4 sec."},
[70408] = {name = "Усиление магии", description = "[q]Усиливает магию, направленную на выбранного участника группы, увеличивая получаемый им магический урон не более чем на 2400 ед. и усиливая эффекты лечения на нем не более чем на 3600 ед. Время действия – 30 sec."},
[70409] = {name = "Огненный шар", description = "[q]Наносит цели 11100 to 12900 ед. урона от огня."},
[70410] = {name = "Превращение в паука", description = "[q]Превращает противника в паука, который растерянно ползает в течение 12 sec. В это время паук не может атаковать и произносить заклинания, но очень быстро восстанавливает здоровье. При получении урона противник принимает свой прежний облик. Единовременно эффект превращения можно наложить только на одну цель. Действует только на животных, гуманоидов и безобидных существ."},
[70421] = {name = "Снежная буря", description = "[q]Начинается снежная буря, длящаяся 10 sec. и наносящая 43 ед. урона от магии льда раз в 2 сек. всем противникам в зоне поражения."},
[70423] = {name = "Проклятие вампира", description = "[q]При прямом исцелении будет наложен эффект \"Порченая кровь\". Время действия – 20 sec."},
[70425] = {name = "Цепное исцеление", description = "[q]Наполняет раненого союзника целительной энергией, которая затем переходит на другого союзника, находящегося поблизости. Заклинание может исцелить до 3 целей."},
[70431] = {name = "Шаг сквозь тень", description = "[q]Пройти сквозь тень, чтобы появиться за спиной у противника."},
[70432] = {name = "Кровавое ошеломление", description = "[q]Оглушает противника и наносит 3250 ед. урона от кровотечения раз в 2 сек."},
[70435] = {name = "Раздирание плоти", description = "[q]Наносит противнику 100% урона от оружия и заставляет его истекать кровью, нанося дополнительно 4375 to 5625 ед. урона раз в 2 сек. в течение 30 sec."},
[70437] = {name = "Нечестивый удар", description = "[q]Мгновенная жестокая атака, наносящая 150% от урона оружия."},
[70447] = {name = "Выделения неустойчивого слизнюка", description = "[q]Липкие выделения приковывают цель к месту, нанося 7313 to 7687 ед. урона раз в 1 сек. К цели начинает двигаться неустойчивый слизнюк и, добравшись до нее, взрывается, нанося 243750 to 256250 ед. урона. Урон делится между всеми находящимися рядом целями."},
[70449] = {name = "Вампирский раж", description = "[q]Рывок к противнику, наносящий 5550 to 6450 ед. урона цели и ее ближайшим союзниками и отбрасывающий их назад."},
[70450] = {name = "Кровавое зеркало", description = "[q]На вас отражается 0% урона, получаемого связанной с вами целью."},
[70460] = {name = "Струи холодного пламени", description = "[q]Наносят урон от магии льда всем, кто встречается на пути."},
[70464] = {name = "Град ледяных копий", description = "[q]Наносит 13200 to 19800 ед. урона всем противникам в радиусе 80 м. Наносит втрое больше урона замороженным целям."},
[70475] = {name = "Тучный рой насекомых", description = "[q]Наносит урон в количестве 5% от максимального запаса здоровья раз 1 сек. всем игрокам в выбранной области."},
[70480] = {name = "Освобождение духа", description = nil},
[70481] = {name = "Освобождение духа", description = nil},
[70482] = {name = "Освобождение духа", description = nil},
[70492] = {name = "Взрыв слизнюка", description = "[q]Наносит 243750 to 256250 ед. урона, который распределяется между находящимися рядом противниками. Разбрасывает противников во все стороны!"},
[70498] = {name = "Призыв зловещих духов", description = "[q]Призывает 10 зловещих духов, атакующих противников через 30 сек."},
[70503] = {name = "Импульс духа", description = "[q]Заклинатель жертвует собой, нанося 35100 to 36900 ед. урона от темной магии всем противникам в радиусе 5 м."},
[70512] = {name = "Визг высокорожденных", description = "[q]Издает крик боли, причиняющий 7200 to 8800 ед. урона от темной магии всем противникам, находящимся перед заклинателем."},
[70513] = {name = "Залп", description = "[q]Обрушивает на противника и его ближайших союзников град стрел. Поражает до 3 целей, нанося им обычный урон, увеличенный на 75."},
[70516] = {name = "Огненная глыба", description = "[q]Наносит противнику урон от огня."},
[70517] = {name = "Тотем исцеляющего потока", description = "[q]Заклинатель создает рядом с собой тотем исцеляющего потока с прочностью 5 ед. и временем существования 1 min., который исцеляет участников группы на расстоянии 20 м на 28 ед. здоровья раз в 2 сек."},
[70521] = {name = "Ледяная бомба", description = "[q]Наносит 0 ед. урона от магии льда всем находящимся рядом целям."},
[70539] = {name = "Рвотный слизнюк", description = "[q]Рвотный слизнюк ошеломляет противника, снижая его скорость передвижения на 50% и нанося 9263 to 9737 ед. урона раз в 2 сек. в течение 20 sec."},
[70541] = {name = "Заражение", description = "[q]Наносит 12723 to 14276 ед. урона от темной магии всем противникам в радиусе 60 м от цели и затем дополнительный нарастающий урон от темной магии раз в секунду. Эффект рассеивается, если уровень здоровья цели становится выше 90%."},
[70542] = {name = "Взмах мутанта", description = "[q]Мгновенно наносит 100% урона от оружия и снижает сопротивляемость цели к физическому урону на 4% на 20 sec. Эффект суммируется до 5 раз."},
[70588] = {name = "Подавление", description = "[q]Снижает эффективность применяемого к цели исцеления на 10% на until cancelled."},
[70589] = {name = "Ледяное прикосновение", description = "[q]Наносит 0 ед. урона от магии льда и уменьшает скорость атаки ближнего боя, дальнего боя и произнесения заклинаний на 35% на until cancelled."},
[70594] = {name = "Стрела смертельной стужи", description = "[q]Наносит цели 11563 to 13437 ед. урона от темной магии льда."},
[70602] = {name = "Порча", description = "[q]Медленно подтачивает разум цели, подчиняя его воле Короля-лича."},
[70633] = {name = "Выброс внутренностей", description = "[q]Наносит 3750 ед. урона от сил природы и увеличивает получаемый физический урон на 25% на 12 sec."},
[70645] = {name = "Изгнание Тьмы", description = "[q]Изгнанный противник не может совершать какие-либо действия, но остается неуязвимым в течение 10 sec. Изгнать можно только одну цель за раз."},
[70659] = {name = "Некротический удар", description = "[q]Удар проклятым клинком наносит 70% урона от оружия и вызывает продолжительную болезнь, которая поглощает 20000 ед. последующего исцеления."},
[70670] = {name = "Теневой рассекающий удар", description = "[q]Наносит 19000 to 21000 ед. урона от темной магии противникам, которые находятся перед заклинателем."},
[70671] = {name = "Обессиливающая гниль", description = "[q]Смрадная гниль наносит цели 4242 to 4758 ед. урона от сил природы раз в 3 сек. и снижает эффективность получаемого лечения на 25%. Время действия – 9 sec."},
[70674] = {name = "Сила вампира", description = "[q]Наполняет заклинателя темной силой, увеличивающей весь наносимый урон на 25% и восстанавливающей ему здоровье в количестве 300% от нанесенного урона."},
[70705] = {name = "Знамя Пылающего Клинка", description = "[q]Штандарт мастера клинка."},
[70714] = {name = "Незыблемая ледяная броня", description = "[q]Глыба льда защищает заклинателя от вражеских атак."},
[70716] = {name = "Мощный удар", description = nil},
[70732] = {name = "Выстрел глефами", description = "[q]Выстреливает в цель большими глефами, наносящими физический урон противникам и отбрасывающими их назад."},
[70735] = {name = "Выстрелить глефу", description = "[q]Выстреливает в цель большую глефу, наносящую физический урон противникам и отбрасывающий их назад."},
[70744] = {name = "Кислотный взрыв", description = "[q]Заклинатель взрывается, нанося 17672 to 19828 ед. урона от сил природы мгновенно и 1250 ед. урона от сил природы в секунду всем противникам в радиусе 15 м. Время действия – 20 sec."},
[70746] = {name = "Телепорт в Солнечный Колодец", description = nil},
[70750] = {name = "Боевой крик", description = "[q]Увеличение урон, наносимый ближайшими членами группы, на 50% и скорость их атаки на 20% в течение 30 sec."},
[70751] = {name = "Коррозия", description = "[q]Каждая удачная атака ближнего боя наносит дополнительно 3125 ед. урона от сил природы раз в 3 сек. и ослабляет броню на 10%. Эффект суммируется до 5 раз. Время действия – 6 sec."},
[70754] = {name = "Огненный шар", description = "[q]Наносит противнику 5891 to 6609 ед. урона от огня."},
[70759] = {name = "Залп ледяных стрел", description = "[q]Заклинание наносит ближайшим противникам 4713 to 5287 ед. урона от магии льда, снижая скорость их передвижения на 50% в течение 8 sec."},
[70768] = {name = "Оккультная защита", description = "[q]Окружает заклинателя устойчивым щитом, который отражает любые атакующие заклинания, защищает от эффектов прерывания и поглощает до 50000 ед. урона."},
[70782] = {name = "Прикосновение вурдалака", description = "[q]Снижает скорость передвижения цели на 20%. Суммируется до 4 раз."},
[70794] = {name = "Эффект замораживающей ловушки", description = "[q]Установка ловушки, которая замораживает первого приблизившегося противника, лишая его возможности совершать какие-либо действия в течение 1 min. Если цель получает урон, лед разбивается. Устанавливается на 1 min. Единовременно может быть установлена только одна ловушка."},
[70796] = {name = "Прицельный выстрел", description = "[q]Прицельный выстрел, наносящий тяжелый урон."},
[70842] = {name = "Барьер маны", description = "[q]Окружает заклинателя устойчивым щитом, постоянно восстанавливающим потерянное здоровье, расходуя при этом ману."},
[70853] = {name = "Вязкая гадость", description = "[q]Наносит 23400 to 24600 ед. урона в радиусе 5 м и снижает скорость атаки и произнесения заклинаний на 250% на 15 sec."},
[70864] = {name = "Отмщение Вегарда", description = nil},
[70866] = {name = "Теневой разряд", description = "[q]Наносит 4500 to 5500 ед. урона от темной магии всем противникам в радиусе 5 м, снижая их скорость передвижения на 70% и отбрасывая их назад."},
[70867] = {name = "Сущность Кровавой королевы", description = "[q]Вас наполняет кровь королевы вампиров. Наносимый урон увеличен на 100%.\n При атаке вы восстанавливаете здоровье."},
[70873] = {name = "Изумрудная энергия", description = "[q]Питает цель мечтами, восполняя ей 200 ед. маны раз в 3 сек. Урон и исцеление, исходящие от цели, также усиливаются на 10%. Суммируется до 100 раз. Время действия – 40 sec."},
