[71553] = {name = "Рывок", description = "[q]Рывок к противнику и оглушение его на 1.50 sec."},
[71554] = {name = "Раскол брони", description = "[q]Удар, повреждающий доспех противника. Ослабляет броню на 4%, может суммироваться до 5 раз и действует в течение 30 sec. Удар также значительно повышает угрозу (уровень угрозы прямо зависит от силы атаки)."},
[71590] = {name = "Ракетомет", description = "[q]Выстрел мощной ракетой, которая наносит 960 to 1440 ед. урона и оглушает противника на 3 сек. Однако у этой штуки мощный откат..."},
[71592] = {name = "Бомба из оскверненного железа", description = "[q]Наносит 330 to 770 ед. урона от огня и оглушает противников в радиусе 5 м на 2 sec. Любой урон цели выводит ее из состояния. Может не работать против целей уровня 76 и выше."},
[71594] = {name = "Пулемет", description = "[q]Наведение пулемета на противника. Наводка не сбивается в течение 6 sec., даже если цель двигается."},
[71595] = {name = "Быстрое исцеление", description = "[q]Восстанавливает 8325 to 9675 ед. здоровья союзнику."},
[71603] = {name = "Сила мутанта", description = "[q]Увеличивает наносимый урон на 50%, повышает скорость атаки на 50%. aПри атаке мутация прогрессирует, нанося заклинателю увеличивающийся урон раз в 3 сек."},
[71612] = {name = "Призыв видения Чарлги", description = nil},
[71614] = {name = "Ледяной замок", description = "[q]Сковывает цель льдом, делая ее невосприимчивой ко всему и лишая возможности действовать."},
[71615] = {name = "Слезоточивый газ", description = "[q]От слезоточивого газа вы испытываете головокружение и не можете двигаться!"},
[71621] = {name = "Создание варева", description = "[q]Профессор Мерзоцид обшаривает лабораторный стол в поисках мощного зелья!"},
[71623] = {name = "Безумный выпад", description = "[q]Наносит противнику 75% урона от оружия и заставляет его истекать кровью, нанося дополнительно 7000 to 9000 ед. урона раз в 3 сек. в течение 15 sec."},
[71656] = {name = "Разговор с Манкриком", description = nil},
[71657] = {name = "Обстрел взрывчатыми яйцами", description = "[q]Забрасывает противников Механоцыпа смертельно опасными взрывчатыми яйцами, нанося 14 to 21 ед. урона от огня. Поражает всех противников в пределах 5 м от основной цели Механоцыпа."},
[71658] = {name = "Залп взрывчатыми яйцами", description = "[q]Выстреливает смертоносный заряд взрывчатых яиц в Молота, нанося 285 to 365 ед. урона от огня. Оборонная система механоцыпа в действии!"},
[71659] = {name = "Мощный лазерный луч", description = "[q]Наносит Молоту 190 to 210 ед. урона от огня. Любой акуле нужен мощный лазерный луч!"},
[71660] = {name = "Ремонт", description = "[q]Восстанавливает 200 ед. прочности паровой мехакулы XD. Это вам пригодится!"},
[71701] = {name = "Nightmare Mass Aura Cancel", description = nil},
[71705] = {name = "Призыв боевого ястреба", description = "[q]Призыв ястреба для помощи заклинателю в бою до тех пор, пока он не разрешит удалиться."},
[71711] = {name = "Вызов поганища Грея", description = nil},
[71718] = {name = "Колдовское пламя", description = "[q]Пламенный шар летит в выбранную область и взрывается при приземлении."},
[71736] = {name = "Вампирская аура", description = "[q]Атаки ближнего боя восстанавливают атакующему 9000 to 11000 ед. здоровья."},
[71769] = {name = "Воскрешение мертвых", description = "[q]Возвращает мертвецов к жизни, превращая их в нежить – слуг Короля-лича."},
[71772] = {name = "Кровавый вихрь", description = "[q]Вызов буйного вихря кровавых стрел."},
[71774] = {name = "Мощный удар", description = nil},
[71784] = {name = "Молот предательства", description = "[q]Оглушение цели на 6 sec."},
[71785] = {name = "Воспламенение", description = "[q]Поджигает противника, нанося 3000 ед. урона от огня за 10 sec. и приводя его в панику. Пока цель пылает, пламя периодически обжигает ближайших союзников цели, нанося 150 ед. урона."},
[71792] = {name = "Ядовитый плевок", description = "[q]Наносит противнику 47 to 53 ед. урона от сил природы и дополнительно 20 ед. урона от сил природы раз в 3 сек. в течение 15 sec."},
[71801] = {name = "Суета", description = "[q]Увеличивает урон от следующей атаки заклинателя в ближнем бою на 50%."},
[71805] = {name = "Чумной дух", description = "[q]Убийственная вонь наносит 1500 ед. урона от сил природы всем находящимся рядом противникам."},
[71807] = {name = "Ослепительные искры", description = "[q]С пальцев заклинателя срываются ослепительные искры, нанося 16720 to 18480 ед. урона от огня в течение 8 sec. всем противникам в конусе поражения и снижая их скорость передвижения на 40%."},
[71812] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей 13 to 16 ед. урона от темной магии."},
[71815] = {name = "Копье зловещих теней", description = "[q]Поражение противника стрелой тьмы, наносящей 95063 to 99937 ед. урона от темной магии."},
[71822] = {name = "Теневой резонанс", description = "[q]Ближайшая цель входит в резонанс с тенями, получая 1000 ед. урона. Получаемый ею урон от темной магии при этом уменьшается на 35% на until cancelled."},
[71841] = {name = "Кара", description = "[q]Покарать противника, нанеся 1591 to 1785 ед. урона от светлой магии и восстановив себе 25% от максимального объема здоровья."},
[71842] = {name = "Кара", description = "[q]Покарать противника, нанеся 1804 to 2022 ед. урона от светлой магии и восстановив себе 25% от максимального объема здоровья."},
[71861] = {name = "Роящиеся тени", description = "[q]Цель поглощает темная бездна. Множество теней появляется под целью раз в 0.5 сек."},
[71893] = {name = "Поглощение зелий", description = "[q]Профессор Мерзоцид принимается пить все зелья подряд!"},
[71906] = {name = "Страждущая душа", description = "[q]Вырывание души цели из тела. Темная сторона души принимает вид призрачного существа, подчиняющегося заклинателю. Действует until cancelled."},
[71921] = {name = "Дикий скачок", description = nil},
[71923] = {name = "Кровавая чума", description = "[q]Болезнь, наносящая урон от темной магии в течение 12 sec."},
[71924] = {name = "Удар чумы", description = "[q]Жестокий удар, наносящий 60% урона от оружия, 27 ед. урона и заражающий цель кровавой чумой – болезнью, наносящей периодический урон от темной магии. Удар отменяет один эффект периодического лечения, наложенный на цель."},
[71925] = {name = "Увечье", description = "[q]Увечит цель, нанося ей 100% обычного урона и дополнительно 25, кроме того цель получает 30% дополнительного урона от эффекта приемов \"Трепка\", \"Полоснуть\" и кровотечения на 12 sec. Длина серии приемов увеличивается на 1."},
[71926] = {name = "Разорвать", description = "[q]Завершающий удар, наносящий периодический урон."},
[71927] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[71928] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[71930] = {name = "Вспышка Света", description = "[q]Призывает силы Света для исцеления союзника."},
[71931] = {name = "Великое исцеление", description = "[q]Светлая магия исцеляет союзника."},
[71932] = {name = "Обновление", description = "[q]Восстанавливает цели 23565 to 26435 ед. здоровья за 15 sec."},
[71933] = {name = "Потрошение", description = "[q]Завершающий удар, наносящий урон."},
[71934] = {name = "Молния", description = "[q]Молния поражает цель, нанося 2334 to 2666 ед. урона от сил природы."},
[71936] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[71937] = {name = "Порча", description = "[q]Нанесение находящимся рядом противникам урона от темной магии раз в 3 сек. в течение 24 sec."},
[71940] = {name = "Изощренные кошмары", description = "[q]Погружает цель в кошмар, нанося ей 200 ед. урона от сил природы раз в секунду. Цель восполняет 200 ед. маны раз в 3 сек. Урон и исцеление, исходящие от цели, усиливаются на 10%. Эффект суммируется до 100 раз. Время действия – 40 sec."},
[71943] = {name = "Теневой резонанс", description = "[q]Призыв темного ядра. Ядра питаются собственной внутренней энергией и через некоторое время взрывается."},
[71951] = {name = "Некротический удар", description = "[q]Удар проклятым клинком наносит 4625 to 5375 ед. физического урона и вызывает продолжительную болезнь, которая поглощает 15000 ед. последующего исцеления."},
[71953] = {name = "Сияющая аура", description = "[q]Паладин лучится животворящим светом, восстанавливая находящимся рядом союзникам 3% от их максимального запаса здоровья раз в 3 сек."},
[71955] = {name = "Целенаправленные атаки", description = "[q]Заклинатель запрыгивает на спину противника и начинает атаковать с повышенной скоростью, но при этом не может атаковать другие цели. Время действия – 30 sec."},
[71956] = {name = "Живительные дожди", description = "[q]Восстанавливают 13875 to 16125 ед. здоровья всем находящимся рядом участникам группы раз в 2 сек. в течение 10 sec. Требуется поддержание потока."},
[71960] = {name = "Героический прыжок", description = "[q]Атака в прыжке, наносящая урон всем противникам в радиусе 8 м от выбранной точки и оглушающая их на 2 sec."},
[71965] = {name = "Дождь Хаоса", description = "[q]Дождь огня сжигает всех противников в выбранной области, нанося 142 to 157 ед. урона от магии Хаоса раз в 3 сек. в течение 9 sec."},
[71973] = {name = "Плевок ядом огненной змеи", description = "[q]Наносит цели урон от огня."},
[71974] = {name = "Змеиный страж", description = "[q]Создает рядом с заклинателем опаляющий тотем с прочностью 5 ед. и временем существования 1 min., который периодически атакует противника в радиусе 0 м, нанося 5 ед. урона от огня."},
[71976] = {name = "Исцеляющий поток", description = "[q]Восполняет 50 ед. здоровья раз в 2 сек."},
[71984] = {name = "Тотем исцеляющего потока", description = "[q]Шаман создает рядом с собой тотем исцеляющего потока с прочностью 5 ед. и временем существования 10 min., который восполняет 50 ед. здоровья участникам группы в радиусе 60м раз в 2 сек."},
[71993] = {name = "Ледяной молот", description = "[q]Атаки могут наложить эффект \"Обморожение\", наносящий 2438 to 2562 ед. урона раз в 2 сек. в течение 20 sec."},
[72004] = {name = "Обморожение", description = "[q]Наносит 2438 to 2562 ед. урона от магии льда раз в 2 сек. и снижает скорость передвижения на 5% на 20 sec."},
[72013] = {name = "Углекаменная пушка", description = "[q]Выстреливает в противника огромным пушечным ядром."},
[72014] = {name = "Цепное исцеление", description = "[q]Наполняет раненого союзника целительной энергией, которая затем переходит на другого союзника, находящегося поблизости. Заклинание может исцелить до 3 целей, но с каждой новой целью энергия слабеет на 50%."},
[72034] = {name = "Пурга", description = "[q]Наносит 13163 to 13837 ед. урона от магии льда всем находящимся поблизости противникам и увеличивает получаемый урон от магии льда на 15%."},
[72037] = {name = "Сотрясающий вихрь", description = "[q]Создает рядом с целью энергетический вихрь, который наносит урон ближайшим противникам и отбрасывает их назад."},
[72039] = {name = "Могучий вихрь", description = "[q]Создает рядом со всеми находящимися рядом противниками силовые завихрения, наносящие 5000 ед. физического урона и отбрасывающие противников, находящихся рядом с целью."},
[72040] = {name = "Жаркое колдовское пламя", description = "[q]Шар колдовского пламени летит в выбранную область и взрывается при приземлении."},
[72043] = {name = "Благословение королей", description = "[q]Благословляет дружественную цель, повышая все ее характеристики на 10% на 10 min. На одного игрока единовременно может действовать лишь одно благословение от каждого паладина."},
[72050] = {name = "Огненная глыба", description = "[q]Наносит урон всем противникам в радиусе 5 метров от области воздействия."},
[72052] = {name = "Кинетическая бомба", description = "[q]Взрывается при контакте с землей, нанося 18000 to 22000 ед. физического урона противникам в большой области и отбрасывая их назад."},
[72055] = {name = "Щит поглощения урона", description = "[q]Призванная сила пустоты создает вокруг заклинателя щит, поглощающий 50 ед. урона. Эффект продолжается 5 min. Пока щит активен, произносимые заклинания не прерываются при получении урона."},
[72057] = {name = "Удар лича", description = "[q]Дает цели пощечину холодной рукой, нанося 10500 to 13500 ед. урона от темной магии и отбрасывая ее назад."},
[72065] = {name = "Защитный покров", description = "[q]Защищает от любых физических атак на 6 sec."},
[72066] = {name = "Покров защиты от заклинаний", description = "[q]Защита выбранного участника группы от всех магических атак на 6 sec."},
[72069] = {name = "Солдаты битвы за Гилнеас", description = "[q]Быстрее, лучше, сильнее!"},
[72090] = {name = "Мерзлый грунт", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 5 sec."},
[72091] = {name = "Ледяной шар", description = "[q]Рядом появляется ледяной шар."},
[72114] = {name = "Прицельный выстрел", description = "[q]Прицельный выстрел, увеличивающий урон в дальнем бою на 0 ед."},
[72120] = {name = "Обморожение", description = "[q]Наносит 1950 to 2050 ед. урона от магии льда раз в 2 сек. и снижает скорость передвижения на 5% на 10 sec."},
[72122] = {name = "Ледяной молот", description = "[q]Атаки могут наложить эффект \"Обморожение\", наносящий 1950 to 2050 ед. урона раз в 2 сек. в течение 10 sec."},
[72123] = {name = "Ледяной взрыв", description = "[q]Хранитель льда поражает цель, нанося 20813 to 24187 ед. урона от магии льда."},
[72133] = {name = "Боль и страдание", description = "[q]Наносит 7540 to 8460 ед. урона от темной магии всем противникам в конусе поражения перед заклинателем и дополнительно 1200 ед. урона от темной магии каждую секунду в течение 3 sec."},
[72143] = {name = "Исступление", description = "[q]Увеличивает наносимый физический урон на 200%."},
[72149] = {name = "Ударная волна", description = "[q]Силовая волна наносит 150% урона от оружия и оглушает всех противников, находящихся в конусе поражения перед заклинателем на расстоянии 20 м."},
[72155] = {name = "Получение образца гнили", description = nil},
[72163] = {name = "Огненный шар", description = "[q]Бросок огненного шара, который наносит 6175 to 6825 уд. урона от огня."},
[72166] = {name = "Ледяная стрела", description = "[q]Наносит противнику 5225 to 5775 ед. урона от магии льда и снижает скорость его передвижения на 50% на 4 sec."},
[72169] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке, нанося 3932 to 4568 ед. урона от огня и дополнительно 5400 ед. урона от огня в течение 6 sec."},
[72171] = {name = "Ледяные оковы", description = "[q]Сковывание противника ледяными цепями, не позволяющими ему двигаться с места в течение 6 sec."},
[72172] = {name = "Призыв кровавых чудовищ", description = "[q]Два кровавых чудовища приходят по зову Саурфанга."},
[72176] = {name = "Узы крови", description = "[q]Когда это существо наносит урон, Саурфанга Смертоносного наполняет сила крови."},
[72194] = {name = "Удар щитом", description = "[q]Наносит противнику сильный удар щитом. Противник получает физический урон и прерывает чтение заклинания."},
[72198] = {name = "Спектральный разящий удар", description = "[q]Спектральный удар игнорирует броню и наносит 100% обычного урона от оружия."},
[72203] = {name = "Мученическое исступление", description = "[q]Повышает скорость атаки в ближнем бою на 100%."},
[72206] = {name = "Выстрел из пушки", description = "[q]Выстрелить из пушки по пигмеям Умлота, находящимся в радиусе 100 м."},
[72208] = {name = "Выстрел", description = "[q]Выстрел в противника, наносящий 4163 to 4837 ед. физического урона."},
[72215] = {name = "Ледяная ловушка", description = "[q]Размещение ледяной ловушки, которая создает вокруг себя толстый слой льда на 30 sec., когда к ней приближается первый противник. Все противники в радиусе 10 м замедляются на 50%, пока остаются в зоне действия эффекта. Ловушка существует в течение 1 min. Единовременно может быть установлена только одна ловушка."},
[72219] = {name = "Вздутие желудка", description = "[q]Наносит 14625 to 15375 ед. урона и накладывает на цель эффект \"Вздутие желудка\", увеличивающий наносимый урон на 10% на 1.67 min. При 10-кратном суммировании вызывает желудочный взрыв, наносящий огромный урон ближайшим союзникам цели."},
[72222] = {name = "Проклятая стрела", description = "[q]Проклятая стрела увеличивает получаемый целью магический урон на 50%."},
[72232] = {name = "Ослабленный дух", description = "[q]Душа цели ослаблена заклинанием \"Слово силы: Щит\" и не может быть повторно защищена в течение 1 min."},
[72244] = {name = "Смерч Итиса", description = "[q]Перенос цели на вершину ближайшей скалы."},
[72262] = {name = "Сотрясение", description = "[q]Сотрясает землю, из-за чего края платформы начинают осыпаться."},
[72268] = {name = "Леденящий выстрел", description = "[q]Наносит цели 5700 to 6300 ед. урона от магии льда и оглушает ее на 2 sec."},
[72272] = {name = "Губительный газ", description = "[q]Поражает выбранную область губительной чумой, наносящей 4875 to 5125 ед. урона раз в 2 сек. в течение 6 sec. Зараженные цели страдают рвотой, нанося 5850 to 6150 ед. урона ближайшим союзникам."},
[72293] = {name = "Метка падшего воителя", description = "[q]Атаки Саурфанга Смертоносного в ближнем бою также задевают помеченную цель, нанося дополнительный физический урон. Если цель умирает, находясь под действием этого эффекта, Саурфанг восстанавливает 20% от своего максимального запаса здоровья."},
[72294] = {name = "Пожирание беженцев", description = "[q]Пожирание тел погибших странников."},
[72318] = {name = "Слово Тьмы: Боль", description = "[q]Произнесение слова Тьмы, которое наносит противнику урон от темной магии раз в 2 сек. в течение 8 sec."},
[72320] = {name = "Кольцо разрушения", description = "[q]Наносит 3194 to 3806 ед. урона от темной магии и отбрасывает назад противников в радиусе 10 м от цели."},
[72321] = {name = "Съеживание от страха", description = "[q]Вселяет страх в противника, заставляя его съежиться от ужаса на 4 sec."},
[72322] = {name = "Исцеление тьмой", description = "[q]Исцеляет союзника магией Тьмы на 50875 to 59125."},
[72326] = {name = "Шаг сквозь тень", description = "[q]Заклинатель проходит сквозь тени, появляясь за спиной у цели, и наносит ей 7000 ед. урона от темной магии."},
[72328] = {name = "BFGC - Attempt to Start", description = nil},
[72329] = {name = "Смертельный яд", description = "[q]Наносит 1065 ед. урона от сил природы раз в 3 сек. в течение 12 sec. Суммируется до 5 раз на одной цели."},
[72333] = {name = "Бросок отравленного кинжала", description = "[q]Наносит урон от сил природы раз в 3 сек. и снижает скорость передвижения на 15% на 12 sec."},
[72335] = {name = "Удар по почкам", description = "[q]Подлый удар, оглушающий противника на 3 sec."},
[72342] = {name = "Галлюцинация", description = "[q]Создает поблизости копию заклинателя, которая применяет заклинания и атакует противников мага. Время действия – 30 sec. После смерти взрывается, нанося 10000 ед. урона от тайной магии находящимся рядом противникам."},
[72343] = {name = "Галлюцинация", description = "[q]Делает заклинателя невидимым и оглушает его на 6 sec."},
[72344] = {name = "Галлюцинация", description = "[q]Наносит 10000 ед. урона от тайной магии всем окружающим противникам в случае смерти."},
[72350] = {name = "Неистовство Ледяной Скорби", description = "[q]Наносит 1000000 ед. урона от темной магии всем противникам и не позволяет им воскреснуть или покинуть тело."},
[72360] = {name = "Уничтожение", description = "[q]Жестокая мгновенная атака, наносящая 26000 ед. урона."},
[72362] = {name = "Колодец скверны", description = "[q]Накладывает эффект \"Прикосновение скверны\" на цели в пределах круга, повышая получаемый ими урон от темной магии."},
[72363] = {name = "Оскверненная плоть", description = "[q]Действует на находящихся рядом противников, уменьшая их максимальный запас здоровья на 50% на 8 sec."},
[72368] = {name = "Разделенные муки", description = "[q]Наносит противнику 2.4e+07 ед. урона от темной магии в течение 12 sec. При рассеивании оставшийся урон разделяется между целью и ее союзниками."},
[72371] = {name = "Сила крови", description = "[q]Саурфанг увеличивается в размере на 1% и наносит на 1% больше урона за каждую накопленную единицу силы крови."},
[72378] = {name = "Кольцо крови", description = "[q]Случайный противник разбрызгивает вокруг себя кровь, нанося 1 ед. физического урона находящимся рядом противникам."},
[72385] = {name = "Кипящая кровь", description = "[q]Заставляет кровь противника закипеть, нанося ему 5000 ед. физического урона раз в 3 сек. в течение 15 sec."},
[72390] = {name = "Безысходность", description = "[q]Снижает урон, наносимый всеми находящимися поблизости противниками, и эффективность их исцеления на 25%."},
[72410] = {name = "Руна крови", description = "[q]При атаке в ближнем бою Саурфанг вытягивает здоровье цели и восстанавливает собственное в многократном размере."},
[72422] = {name = "Вибрирующий удар", description = "[q]Ужасающей силы удар наносит 100% обычного урона от оружия ближнего боя и снижает вероятность уклонения цели на 20% на 5 sec. Удар всегда попадает в цель."},
[72426] = {name = "Надвигающееся отчаяние", description = "[q]Надвигающееся отчаяние погружает противника в оцепенение. Если эффект не рассеять в течение 6 sec., цель будет оглушена на 6 sec."},
[72429] = {name = "Массовое воскрешение", description = "[q]Воскрешает всех павших союзников, находящихся рядом."},
[72435] = {name = "Порочный ужас", description = "[q]Леденящий душу крик вселяет страх в находящихся рядом противников и наносит 4000 ед. урона от темной магии раз в 1 сек. в течение 4 sec."},
[72451] = {name = "Мутировавшая чума", description = "[q]Наносит урон раз в 3 сек."},
[72466] = {name = "Спасение души", description = "[q]Пробуждает в цели искру жизни."},
[72518] = {name = "Вихрь", description = "[q]Заключает цель в неодолимый вихрь."},
[72525] = {name = "Берсерк", description = "[q]Увеличивает наносимый урон на 240%.\n Повышает скорость атаки и произнесения заклинаний на 160%."},
[72591] = {name = "Топот", description = "[q]Оглушает динозавров в пределах 10 м на срок в 2.50 sec. Может подбросить в воздух камни."},
[72595] = {name = "Восстановление души", description = "[q]Оживляет цель, возвращая душу обратно в тело."},
[72681] = {name = "Пасти силитида", description = nil},
[72723] = {name = "Устойчивая кожа", description = "[q]Кожа этого существа чрезвычайно устойчива к внешним воздействиям. Она снижает урон от атак по области на 95% и урон от болезней на 70%."},
[72728] = {name = "Разъедание плоти", description = "[q]Разъедание плоти противника с нанесением урона от сил природы раз в 3 сек. в течение 15 sec."},
[72737] = {name = "Бешенство", description = "[q]Саурфанг впадает в бешенство, и его скорость атаки повышается на 30%."},
[72741] = {name = "Обстрел из орудия", description = "[q]Обстрелять наступающие отряды."},
[72754] = {name = "Осквернение", description = "[q]Наносит урон от темной магии всем противникам в выбранной области. При каждом получении противником урона размер области и количество наносимого урона увеличиваются. Длится 60 сек."},
[72762] = {name = "Осквернение", description = "[q]Оскверняет землю под какой-либо из целей. Любой противник, попавший на оскверненный участок, получит урон от темной магии; сам участок при этом увеличится. Время действия – 30 сек."},
[72769] = {name = "Запах крови", description = "[q]Кровавые чудовища Саурфанга чуют добычу. Скорость передвижения всех находящихся рядом противников снижается на 80%, а наносимый чудовищами урон увеличивается на 300% на 10 sec."},
[72839] = {name = "Горение", description = nil},
