[73510] = {name = "Пронзание разума", description = "[q]Наносит противнику 1213 ед. урона от темной магии льда, снимая с него наложенные жрецом эффекты периодического урона от темной магии.\n\n Повышает вероятность нанести цели критический удар следующим заклинанием \"Взрыв разума\" на 30 %. Суммируется до 3 раз."},
[73529] = {name = "Темная ловушка", description = "[q]Наносит всем противникам в радиусе 10 м 23563 to 26437 ед. урона от темной магии, отбрасывая их назад."},
[73539] = {name = "Создание темной ловушки", description = "[q]Призыв темной ловушки, наносящей 23563 to 26437 ед. урона от темной магии всем врагам в радиусе 10 м. и отбрасывающей их назад."},
[73562] = {name = "Проклятие плоти", description = "[q]Фиглай сделал с этим дворфом что-то ужасное!"},
