[73576] = {name = "Взрыв", description = "[q]Наносит 23563 to 26437 ед. урона от темной магии всем противникам в радиусе 10 м."},
[73599] = {name = "\"Каджа-Кола Полулайт\"", description = "[q]Новая улучшенная \"Каджа-Кола Полулайт\"! Вдвое меньше калорий, вдвое больше ИДЕЙ! (ТМ)"},
[73605] = {name = "\"Каджа-Кола Полулайт\"", description = "[q]Новая улучшенная \"Каджа-Кола Полулайт\"! Вдвое меньше калорий, вдвое больше ИДЕЙ! (ТМ)"},
[73650] = {name = "Восстановление души", description = nil},
[73654] = {name = "Великая жатва душ", description = "[q]Жнец готовится поглотить души всех ближайших противников, поддерживая заклинание, наносящее 4000 ед. урона от темной магии в секунду в течение until cancelled. Если противник еще жив, когда поддержание заклинания окончено, то его душу поглотит Ледяная Скорбь."},
[73740] = {name = "Таран", description = "[q]Таранит всех противников, находящихся перед разрушителем, нанося тем 6000 ед. урона и отбрасывая назад."},
[73757] = {name = "Хождение по воде", description = "[q]Дает цели способность ходить по воде."},
[73841] = {name = "На охоте", description = "[q]Следит за вашим обедом."},
[73851] = {name = "Порыв", description = "[q]Повышает скорость передвижения на 60% на 5 sec."},
[73854] = {name = "Вызов боевых псов", description = "[q]Призыв на помощь заклинателю 2 прирученных гиен:прирученных гиен для сопровождения заклинателя, пока он не разрешит удалиться."},
[73855] = {name = "Раздробление черепа", description = "[q]Оглушает противника на 2 sec."},
[73858] = {name = "Вкус крови", description = "[q]Вытягивает здоровья у случайного противника. Урон, наносимый заклинателем, при этом увеличивается на 15%."},
[73859] = {name = "Удар головой", description = "[q]Наносит ближайшим противникам обычный урон и еще 30 ед. повреждений, отбрасывая их назад."},
[73860] = {name = "Смятие", description = "[q]Наносит ближайшим противникам обычный урон и еще 0 ед. повреждений, отбрасывая их назад."},
[73861] = {name = "Огненная буря", description = "[q]Вызов лавового дождя, обжигающего всех противников на выбранном участке с 13875 to 16125 ед. урона от огня раз в 2 сек. в течение 5 sec."},
[73863] = {name = "Широкий взмах", description = "[q]Наносит противникам в конусе поражения перед заклинателем обычный урон, усиленный на 50 ед."},
[73867] = {name = "Лазерный резак", description = "[q]Наносит 20 ед. урона от огня противнику каждую секунду в течение 3 sec."},
[73868] = {name = "Топот копыт", description = "[q]Растоптать Волка."},
