[73958] = {name = "Искровой разряд", description = "[q]Заряд электричества наполняет участок воды, нанося цели урон от сил природы и оглушая ее на 2 sec."},
[73963] = {name = "Ослепляющий токсин", description = "[q]Покрывает противника слоем ослепляющего токсина, который наносит 5088.8 to 5710.4 ед. урона от сил природы в течение 4 sec."},
[73976] = {name = "Дыхание болезни", description = "[q]Заклинатель обдает противника болезнетворным дыханием, нанося тому непосредственно 19000 to 21000 ед. урона от сил природы и 8550 to 9450 ед. урона от сил природы раз в 2 сек. в течение 10 sec."},
[73998] = {name = "Запрещенный прием", description = "[q]Врезаться в торгового принца, нанеся урон крошшером и дополнительно 2025 to 2475 ед. урона (в зависимости от силы атаки). Продолжает наносить урон в размере 1500 ед. средству передвижения торгового принца в течение 8 sec."},
[74000] = {name = "Выброс токсичных отходов", description = "[q]Наносит выбранному противнику и всем ближайшим к нему 30 ед. урона от сил природы."},
[74003] = {name = "Уменьшение в размерах", description = "[q]Понижает силу противника на 150 и выносливость на 150 на 30 sec."},
[74004] = {name = "Огнемет", description = "[q]Поджигает всех противников в конусообразной зоне поражения перед торговым принцем и наносит дополнительный урон от огня раз в 1 сек. в течение 10 sec."},
[74005] = {name = "Денежная лавина", description = "[q]Атаковать противника лавиной денежных купюр, которая наносит 438 to 562 ед. урона. Радиус действия – 50 м."},
[74019] = {name = "Цепная молния", description = "[q]Близстоящих противников по очереди ударяет молния, наносящая 24 to 29 ед. урона от сил природы. Заклинание поражает до 10 целей."},
[74020] = {name = "Выброс лавы", description = "[q]Обжигает противника сгустком пылающей лавы, наносящим 572 to 728 ед. урона от огня."},
[74021] = {name = "Гром", description = "[q]Призывает разряд молнии, который наполняет вас энергией и наносит урон находящимся рядом противникам в радиусе 10 м. Восстанавливает 5% вашей маны и наносит 794 to 906 ед. урона от сил природы ближайшим противникам."},
[74023] = {name = "Сила Природы", description = nil},
[74039] = {name = "Всепоглощающее пламя", description = "[q]Дыхание красного дракона создает стену пламени, которая наносит урон от огня."},
[74061] = {name = "Пелена ужаса", description = "[q]Ужасная морда Ликантота вселяет в вас страх."},
[74086] = {name = "Уничтожение души", description = "[q]Разрушает души ближайших врагов, убивая их на месте."},
[74101] = {name = "Жгучее пламя", description = "[q]Наносит 5365 to 6235 ед. урона от огня, а затем 1275 to 1725 ед. урона в течение 6 sec."},
[74121] = {name = "Истощающий яд", description = "[q]Покрывает противника слоем истощающего токсина, который наносит 11027 to 12372 ед. урона и замедляет действия на 30%."},
[74122] = {name = "Едкие брызги", description = "[q]Наносит 9262 to 10237 ед. урона от сил природы противникам в радиусе 8 м и ослабляет их броню на 5% на 12 sec."},
[74133] = {name = "Небесная вуаль", description = "[q]Окружает заклинателя щитом, поглощающим до 130000 ед. урона, сжигая вместо здоровья ману. Щит также отражает 20% магического урона."},
[74134] = {name = "Звездный дождь", description = "[q]Обрушивает звездопад на всех противников, который причиняет урон от тайной магии."},
[74136] = {name = "Сверхновая", description = "[q]Изисет создает сверхновую. Все находящиеся поблизости противники, смотревшие на ее, получают 0 ед. урона от тайной магии и слепо бродят по полю боя в течение 4 sec."},
[74143] = {name = "Маленькое \"большое огненное дыхание\"", description = "[q]Наносит 2000 ед. урона от огня в течение 5 sec."},
[74153] = {name = "Катапультирование!", description = nil},
