[74374] = {name = "Чародейский обстрел", description = "[q]Наносит противнику 9500 to 10500 ед. урона от тайной магии."},
[74384] = {name = "Устрашающий рев", description = "[q]Парализует ужасом ближайших противников на 4 sec."},
[74392] = {name = "Вспышка огненной звезды", description = "[q]Наносит 6563 to 8437 ед. урона от огня ближайшим противникам."},
