[74438] = {name = "Пулемет", description = "[q]Наносит противнику 1414 to 1586 ед. урона раз в четверть секунды в течение 3 sec. Игнорирует броню и требует поддержания эффекта."},
[74440] = {name = "Брызги пламени", description = "[q]Наносит 5184 to 5816 ед. урона огнем всем противникам в пределах 15 м перед заклинателем."},
[74453] = {name = "Огненная метка", description = "[q]Отмечает цель для воспламенения."},
[74456] = {name = "Воспламенение", description = "[q]Охватывает огнем выбранного противника и всех его союзников в радиусе 10 м, нанося 8325 to 9675 ед. урона от огня и поджигая на 5 sec."},
[74458] = {name = "Силовой щит XL-1", description = "[q]Неуязвимость ко всем атакам и заклинаниям."},
[74502] = {name = "Ослабляющее прижигание", description = "[q]Прижигает противника, вытягивая силу из него самого и его союзников, находящихся от него в пределах 12 м. Эффект накладывается раз в 2 сек. и снижает наносимый целью, находящейся под действием этого эффекта, урон на 2% за каждый наложенный эффект. Урон, наносимый заклинателем, при этом соответственно повышается."},
[74509] = {name = "Отражающая волна", description = "[q]Отбрасывает ближайших противников назад на значительное расстояние, нанося 4163 to 4837 ед. урона от огня и оглушая их на 3 sec."},
[74524] = {name = "Рассекающий удар", description = "[q]Нанесение противнику и его ближайшим союзникам 75% обычного урона. Действует на 10 цели:целей."},
[74525] = {name = "Огненное дыхание", description = "[q]Наносит 39375 to 50625 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[74531] = {name = "Удар хвостом", description = "[q]Размашистый удар хвостом, поражающий всех противников, находящихся за драконом. Наносит 3063 to 3937 ед. урона и оглушает на 2 sec."},
[74538] = {name = "Сочащиеся ядом клыки", description = "[q]Кислота, капающая со жвал скарабея, наносит 3700 to 4300 ед. урона от сил природы раз в 3 сек. Время действия – 15 sec."},
[74542] = {name = "Рваная рана", description = "[q]Наносит противнику 50% урона от оружия и заставляет его истекать кровью, нанося дополнительно 2625 to 3375 ед. урона раз в 2 сек. в течение 15 sec."},
[74551] = {name = "Раскаленные угли", description = "[q]Призывает горящий уголек!"},
[74552] = {name = "Вызов каменного элементаля", description = "[q]Призыв каменного элементаля!"},
[74561] = {name = "Вызов водного духа", description = "[q]Призыв водного духа."},
[74562] = {name = "Пылающий огонь", description = "[q]Пламя охватывает противника, нанося 4000 ед. урона от огня раз в 2 сек. Каждый раз при получении урона от \"Пылающего огня\" цель получает одну \"Метку пылающего огня\". Когда время действия эффекта истекает или эффект рассеивается, образуется огненное пятно, размер которого прямо пропорционален количеству меток, которое цель успела получить. Этот эффект можно рассеять с помощью заклинаний снятия проклятий или рассеивания магии."},
[74606] = {name = "Громовая поступь", description = "[q]Оглушает до 5 противников на расстоянии до 8 м на 2 sec."},
[74608] = {name = "Прыжковый двигатель", description = "[q]Крошшер взлетает по диагонали вперед с помощью реактивного прыжкового двигателя."},
[74613] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[74634] = {name = "Дрожь земли", description = "[q]Землю сотрясают подземные толчки, наносящие 58500 to 61500 ед. урона и оглушающие на 4 sec."},
[74648] = {name = "Падение метеора", description = "[q]Наносит 75000 to 85000 ед. урона от огня всем противникам в радиусе 9 м."},
[74663] = {name = "Спасение олененка", description = "[q]Выводит олененка к его матери в Рощу Эссины."},
[74670] = {name = "Блиц", description = "[q]Генерал Умбрисс совершает рывок к противникам, нанося физический урон и отбрасывая их назад."},
[74699] = {name = "Проклятие Модгуд", description = "[q]Несущий проклятие Модгуд после смерти заразит всех противников в радиусе 8 м недугом Модгуд."},
[74707] = {name = "Осколочная бомба", description = "[q]Наносит 1913 to 2587 ед. урона от огня и разбивает броню противников в радиусе 10 метров. Время действия – 5 sec."},
[74720] = {name = "Удар", description = "[q]Удар по противнику, наносящий 110% урона от оружия и дезориентирующий его на 2 sec."},
[74721] = {name = "Веревочный аркан", description = "[q]Наносит физический урон и притягивает цель к заклинателю."},
[74724] = {name = "Ледяная стрела", description = "[q]Наносит 39 to 51 ед. урона от магии льда ближайшим противникам, замедляя их скорость передвижения на 4 sec."},
[74725] = {name = "Отравляющий плевок", description = "[q]Нанесение 45 ед. урона от сил природы раз в 2 сек. в течение 8 sec."},
[74726] = {name = "Залп шипов", description = "[q]Шипы, наносящие 39 to 51 ед. урона от сил природы."},
[74728] = {name = "Force Player to Summon Horde Poacher", description = nil},
[74759] = {name = "Бутылка грога", description = "[q]Швыряет в противника бутылку грога, отравляя его и нанося физический урон."},
[74762] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[74764] = {name = "Радиационная бомба", description = "[q]Наносит противнику 1915 to 2585 ед. урона от сил природы в течение 5 sec."},
[74780] = {name = "Star Shard State", description = nil},
[74781] = {name = "Замедляющий крик", description = "[q]Понижает скорость атаки противника на 50% на 5 sec."},
[74785] = {name = "Бросок гаечным ключом", description = "[q]Бросок гаечного ключа в противника, оглушающий его на 3 sec. и наносящий 5100 to 6900 ед. урона."},
[74787] = {name = "Всплеск лавы из-под земли", description = nil},
[74788] = {name = "Кольцо огня", description = "[q]Обжигает ближайших противников, нанося им 400 ед. урона огнем раз в 5 сек."},
[74792] = {name = "Увядание души", description = "[q]Темная энергия концентрируется вокруг противника, нанося 4000 ед. урона от темной магии раз в 2 сек. Каждый раз при получении урона от \"Пожирания души\" цель получает одну \"Метку пожирания\". Когда время действия эффекта истекает или эффект рассеивается, происходит взрыв темной энергии, мощность которого прямо пропорциональна количеству меток, которое цель успела получить. Этот эффект можно рассеять с помощью заклинаний снятия проклятий или рассеивания магии."},
[74806] = {name = "Темное дыхание", description = "[q]Наносит 39375 to 50625 ед. урона от темной магии противникам в конусе поражения перед заклинателем."},
[74811] = {name = "Размах", description = "[q]Яростный замах, наносящий урон цели и трем находящимся поблизости противникам."},
[74813] = {name = "Преисподняя", description = "[q]Окружает заклинателя инфернальной аурой, которая периодически наносит урон от огня ближайшим противникам в течение 8 sec. При этом заклинатель не может двигаться или применять свои навыки."},
[74826] = {name = "Материальность", description = "[q]Раздваивает заклинателя в физическом и сумеречном мирах, позволяя ему наносить и получать нормальный урон в обоих мирах."},
[74837] = {name = "Недуг Модгуд", description = "[q]При высвобождении проклятие Модгуд наносит 19500 to 20500 ед. урона от темной магии раз в 2 сек. противникам в радиусе 8 м, снижая их скорость передвижения на 25%. Время действия – 10 sec."},
[74846] = {name = "Кровавая рана", description = "[q]Жестокая атака, наносящая урон от оружия и дополнительно 7400 to 8600 ед. урона раз в 2 сек. в течение 10 sec. или до тех пор, пока здоровье цели не будет восстановлено до 90%."},
[74853] = {name = "Бешенство", description = "[q]Генерала Умбрисса охватывает ярость. Его скорость атаки увеличивается на 50%, а наносимый физический урон – на 50%."},
[74880] = {name = "Чародейская энергия", description = "[q]Накапливает энергию. Заклинатель высвободит ее силу, накопив максимальное количество."},
[74888] = {name = "Волшебный вихрь", description = "[q]Наносит 22500 to 27500 ед. урона от тайной магии всем противникам в радиусе 10 м, отбрасывая их назад."},
[74908] = {name = "Личная защита", description = "[q]Снижает получаемый от фронтальных атак урон на 99%."},
[74972] = {name = "Серая мгла", description = "[q]Бросает грязь противнику в глаза, снижая вероятность того, что он попадет по цели, на 40% на 10 sec."},
[74973] = {name = "Слово силы: Стойкость", description = "[q]Повышение выносливости союзника на 30 min."},
[74975] = {name = "Вырвать кошелек", description = "[q]Предпринимает попытку ограбить цель... Если ограбление не удается, наносит 115% урона от оружия и приводит цель в замешательство на 15 sec."},
[74976] = {name = "Дезориентирующий рык", description = "[q]Снижает скорость атаки и произнесения заклинаний на 50% за каждый заряд. При каждом использовании любой способности один заряд расходуется. Начальное количество зарядов – 3."},
[74981] = {name = "Два клинка", description = "[q]Начальник кузни берет по оружию в каждую руку, что позволяет ему иногда делать двойной выпад. Его меткость при этом повышается."},
[74984] = {name = "Могучий топот", description = "[q]Создает завал."},
[75000] = {name = "Выбор оружия", description = "[q]Начальник кузни выбирает оружие для сокрушения своих врагов."},
[75002] = {name = "Стремительный бросок", description = "[q]Прыжок на цель, наносящий 100% урона от оружия."},
[75004] = {name = "Тиски", description = "[q]Наносит 115% урона от оружия, оставляя болезненную язву. Время действия – 10 sec."},
[75007] = {name = "Обременение", description = "[q]Вес этой булавы поистине огромен. Скорость передвижения того, кто ее использует, понижается на 70%, а наносимый им урон увеличивается на 200%."},
[75008] = {name = "Наскок", description = "[q]Нанесение стандартного урона оружия и оглушение противника."},
[75014] = {name = "Рев безумия", description = "[q]Снижение силы атаки ближнего боя находящихся рядом противников на 30 sec."},
[75015] = {name = "Проклятие тени", description = "[q]Проклинает цель на 10 sec., снижая сопротивление темной магии и тайной магии на 50 ед. и увеличивая получаемый от них урон на 100%."},
[75016] = {name = "Оскверненная земля", description = "[q]Оскверняет землю под заклинателем, нанося 15 ед. урона от темной магии в течение 15 sec. всем противникам, ступившим на эту землю."},
[75017] = {name = "Проклятие рока", description = "[q]Проклинает цель, нанося ей урон от темной магии через 12 sec."},
[75019] = {name = "Ярость сумерек", description = "[q]Выстреливает в противника стрелу, сплетенную из магии, которая наносит темный урон."},
[75021] = {name = "Радужный взгляд", description = "[q]На цель накладывается проклятие, на 30 sec. снижающее сопротивление магии огня, льда и силам природы на 50 ед. и увеличивающее получаемый от них урон на 10%. Чернокнижник может наложить на каждую цель только одно проклятие."},
[75023] = {name = "Бросок булыжника", description = "[q]Валун, наносящий физический урон противнику."},
[75024] = {name = "Огненное буйство", description = "[q]Печать огня наносит урон от огня раз в 1 сек. в течение 8 sec."},
[75054] = {name = "Сумеречная порча", description = "[q]Искажает цель темной магией."},
[75056] = {name = "Пронзающий рывок", description = "[q]Совершает рывок к противнику, пронзая его и нанося 9750 to 10250 ед. физического урона раз в 1 сек. в течение 5 sec."},
[75059] = {name = "Бронебой", description = "[q]Ослабление брони противника на 50% на 15 sec.\n Снижение наносимого противником урона на 35% на 15 sec."},
[75060] = {name = "Предельная концентрация", description = "[q]Следующая атака заклинателя в ближнем бою нанесет критический урон."},
[75061] = {name = "Глоток порчи", description = "[q]Насылает на противника порчу, наносящую урон от темной магии раз в 3 сек. Время действия – 15 sec."},
[75062] = {name = "Кольцо льда", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 8 sec."},
