[75824] = {name = "Enforcers Ready", description = nil},
[75833] = {name = "Морозная пушка", description = "[q]Берет в цель гоночную баржу Пшикса и Поззика. Сопутствующие повреждения печальны, но неизбежны."},
[75842] = {name = "Ртутная броня", description = "[q]При обычной температуре ртуть поглощает урон от вражеских атак."},
[75846] = {name = "Перегретая ртутная броня", description = "[q]При нагревании ударопрочность ртути снижается, однако все находящиеся рядом противники получают урон, а атаки наносят дополнительный урон от огня."},
[75849] = {name = "Выстрел из пушки", description = "[q]Стреляет из пушки по шлюпке абордажника Южных морей."},
[75861] = {name = "Связующие тени", description = "[q]Вытягивают из цели 7800 to 8200 ед. здоровья раз в 1 сек. и оплетают противников в радиусе 8 м от нее на 6 sec."},
[75888] = {name = "Пробуждение пламени", description = "[q]Раскаленный воздух увеличивает запас здоровья всех живых огней в радиусе 15 м и наносимый ими урон на 30%."},
[75903] = {name = "Темное сокрушение", description = "[q]Выстреливает в случайную цель стрелу. Стрела наносит урон от темной магии всем противникам в пределах 5 м от места попадания."},
[75907] = {name = "Удар по дуге", description = "[q]Наносит 150% урона от оружия в 5-метровом конусе перед заклинателем. От эффекта невозможно уклониться, также он не может быть парирован или блокирован."},
[75924] = {name = "Шок разочарования", description = "[q]Оглушает противника, делая его неспособным передвигаться и атаковать в течение 4 sec."},
[75930] = {name = "Увечье", description = "[q]Наносит противнику физический дополнительный урон в течение 15 sec."},
[75934] = {name = "Бросок копья", description = "[q]Бросает в противника отравленное копье, нанося физический урон и дополнительный урон от сил природы раз в 3 сек. в течение 15 sec."},
[75940] = {name = "Спокойствие", description = "[q]Восстанавливает здоровье всех ближайших дружественных целей на 41625 to 48375 ед. раз в 2 сек. в течение 8 sec. Требуется поддерживать заклинание."},
[75942] = {name = "Наваждение вуду", description = "[q]Создает поблизости копию заклинателя, которая будет произносить заклинания и атаковать противников мага. Длится 30 sec."},
[75945] = {name = "Гнев бури", description = "[q]Создание в указанной области неистового шторма, наносящего противникам урон от сил природы раз в 1 сек. и увеличивающая время между атаками противников на 67%. Время действия – 10 sec."},
[75946] = {name = "Огонь и пламя", description = "[q]Поджигает противника, а затем наносит дополнительный урон от огня раз в 3 сек. в течение 12 sec."},
[75958] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[75960] = {name = "Удар кнутом", description = "[q]Мощная атака, увеличивающая урон в ближнем бою на 10% и сильно повышающая уровень угрозы."},
[75961] = {name = "Оплетающий выстрел", description = "[q]Наносит 15080 to 16920 ед. урона от сил природы и дополнительно 15200 to 16800 ед. урона от сил природы раз в 2 сек.\n Оплетает корнями."},
[75962] = {name = "Вспарывание брони", description = "[q]Разбивает броню противника, ослабляя ее на 50% в течение 8 sec."},
[75964] = {name = "Убой", description = "[q]Нанесение противнику 24 to 29 ед. урона и оглушение его на 2 sec."},
[75968] = {name = "Круговая раздача", description = "[q]В вихре атакует ближайших противников и наносит обычный урон. Время действия – 15 sec."},
[75986] = {name = "Отпор", description = "[q]Отталкивает назад всех противников, находящихся перед заклинателем."},
[75992] = {name = "Выброс тока", description = "[q]По истечении 5 sec. цель наносит всем находящимся рядом союзникам 56550 to 63450 ед. урона от сил природы."},
[75998] = {name = "Исступление", description = "[q]Увеличение скорости атаки заклинателя на 50% на 1 min."},
[76001] = {name = "Споры грибов", description = "[q]Бросок в случайную цель ядовитых грибных спор, которые при попадании взрываются. Радиус взрыва – 5 м. Любые цели, оказавшиеся в пределах этого радиуса, получают 15000 ед. урона от сил природы раз в 3 сек. в течение 15 sec."},
[76008] = {name = "Шоковый удар", description = "[q]Цель поражает молния, наносящая 70687 to 79312 ед. урона от сил природы."},
[76014] = {name = "Щит из зазубренного камня", description = "[q]Каменный щит отражает 50% получаемого урона."},
[76026] = {name = "Сдавливание", description = "[q]Хватает случайного противника и сдавливает его, нанося тем самым 12500 ед. урона от темной магии раз в секунду в течение 6 sec."},
[76028] = {name = "Ужасающий рев", description = "[q]Находящиеся поблизости противники в ужасе разбегаются на 5 sec."},
[76030] = {name = "Атака берсерка", description = "[q]Бросок на противника, отбрасывающий его назад и наносящий 15725 to 18275 ед. физического урона."},
[76031] = {name = "Сгусток магмы", description = "[q]Наносит противнику 3400 to 4600 ед. урона от огня и поджигает его, нанося дополнительно 4000 ед. урона от огня раз в 3 сек. При снятии эффекта происходит взрыв."},
[76032] = {name = "Огненный разрыв", description = "[q]Наносит 11100 to 12900 ед. урона от огня ближайшим противникам, отбрасывая их назад."},
[76036] = {name = "Клинки силы воды", description = "[q]Атаки клинками могут вызвать цунами, наносящее 25 ед. урона противникам в конусе поражения перед заклинателем."},
[76043] = {name = "Засуха", description = "[q]Снижает скорость атаки и произнесения заклинаний противника на 60% и замедляет его скорость передвижения на 60%. Длительность – 10 sec."},
[76044] = {name = "Режущий шип", description = "[q]Наносит противнику 150% урона от оружия и заставляет его истекать кровью, нанося дополнительно 3640 to 4680 ед. урона раз в 2 сек. в течение 15 sec."},
[76047] = {name = "Темный разлом", description = "[q]Улток бьет по земле, нанося 56550 to 63450 ед. урона от темной магии всем противникам в радиусе 1 м от места воздействия. Образовавшийся темный разлом наносит всем противникам в радиусе 6 м 9425 to 10575 ед. урона от темной магии раз в секунду и со временем увеличивается в размере. Время действия – 60 сек."},
[76086] = {name = "Пылающие кулаки", description = "[q]С некоторой вероятностью при атаке накладывают на противника эффект \"Жгучая рана\"!"},
[76093] = {name = "Жгучая рана", description = "[q]Наносит 8288 to 8712 ед. урона от огня раз в 1 сек. в течение 5 sec."},
[76094] = {name = "Проклятие усталости", description = "[q]Накладывается на случайную цель, снижая скорость ее передвижения на 50%, а скорость произнесения заклинаний и атаки в ближнем и дальнем бою – на 50%. Время действия – 15 sec."},
[76100] = {name = "Исступление", description = "[q]Заклинателя охватывает ярость, увеличивающая наносимый им физический урон на 50% на 10 sec."},
[76133] = {name = "Гнев прилива", description = "[q]Наделяет находящихся рядом союзников невероятной силой, увеличивая запас здоровья, наносимый урон и эффективность исцеляющих заклинаний."},
[76134] = {name = "Лавовый удар", description = "[q]Наносит урон от огня на небольшом участке."},
[76136] = {name = "Кислотная испарина", description = "[q]Вероятность 25% получить урон от сил природы при получении удара."},
[76137] = {name = "Кислотная испарина", description = "[q]Наносит урон от сил природы раз в секунду в течение 4 sec."},
[76146] = {name = "Залп стрел Тьмы", description = "[q]Поражает противника темной молнией, наносящей урон от темной магии."},
[76151] = {name = "Дезориентация", description = "[q]Оглушение цели на 15 sec."},
[76158] = {name = "Пирогенез", description = "[q]Уменьшает урон, наносимый заклинаниями и эффектами огня, на 50%."},
[76159] = {name = "Пирогенез", description = "[q]Уменьшает урон, наносимый заклинаниями и эффектами огня, на 75%."},
[76160] = {name = "Пламенное рассеивание", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам."},
[76165] = {name = "Горячая зола", description = "[q]Наносит 100% урона от оружия в качестве урона от огня и покрывает цель горячей золой, которая наносит 1885 to 2115 ед. урона от огня, когда цель получает урон в ближнем бою. Время действия – 10 sec."},
[76170] = {name = "Выплеск магмы", description = "[q]Наносит 18850 to 21150 ед. урона от огня в конусе поражения перед заклинателем. Кроме того, противники будут получать 2000 ед. урона от огня раз в секунду в течение 10 sec."},
[76171] = {name = "Лавовый выстрел", description = "[q]Бросает в противника раскаленную лаву, которая наносит 56550 to 63450 ед. урона от огня и отбрасывает его назад."},
[76184] = {name = "Альфа-лучи", description = "[q]Альфа-лучи концентрируются на случайно выбранной цели, нанося 14625 to 15375 ед. урона противникам в радиусе 5 м. от заданной точки раз в 0.5 сек. в течение 3 sec."},
[76186] = {name = "Удар грома", description = "[q]Наносит 11250 to 12750 ед. урона от сил природы находящимся рядом противникам, снижая их скорость передвижения на 50% на 8 sec."},
[76189] = {name = "Сумеречная пелена", description = "[q]Снижает эффективность получаемого исцеления на 24% на 4 sec."},
[76205] = {name = "Дыхание изумрудного ткача пламени", description = nil},
[76207] = {name = "Порабощение", description = "[q]Захватывает разум случайного противника. На время действия эффекта у жертвы увеличивается запас здоровья, наносимый урон и эффективность исцеления. Эффект действует 30 sec. или пока здоровье жертвы не упадет до 50%."},
[76218] = {name = "Удар призрачного волка", description = "[q]Разгневанный бог-волк наносит своим врагам от 2399 до 3601 ед. урона."},
[76232] = {name = "Неистовство Бури", description = "[q]Получаемый целью магический урон увеличен на 2000%."},
[76233] = {name = "Ярость земли", description = "[q]Увеличивает получаемый целью физический урон на 2000%."},
[76234] = {name = "Затуманенный разум", description = "[q]Создает туман вокруг заклинателя радиусом 10 м. Все противники, находящиеся в тумане, теряют способность атаковать и произносить заклинания, а также получают 471 to 528 ед. урона от темной магии раз в 0,5 сек. Время действия – 20 сек."},
[76246] = {name = "Жгучее пламя", description = "[q]Невыносимый жар от присутствия Рагнароса наносит 875 to 1125 ед. урона от огня всем ближайшим противникам."},
[76303] = {name = "Сумеречная защита", description = "[q]Окруженный сумеречной аурой, Драгх получает на 90% меньше урона!"},
[76307] = {name = "Поглощение магии", description = "[q]Защищает заклинателя щитом, поглощающим весь урон от заклинаний. Кроме того, каждый раз при получении урона от заклинаний заклинатель будет исцелен на количество поглощенного урона, умноженное на три. Время действия – 5 sec."},
[76312] = {name = "Содрогание земли", description = "[q]Пепельный Молот мощно ударяет об землю, оглушая своих врагов!"},
[76314] = {name = "Пылающий сумеречный щит", description = "[q]Укрывает ближайшего союзника. Атакующие его противники рискуют воспламениться. Щит периодически восстанавливает 34125 to 35875 ед. здоровья раз в 1 в течение 6 sec."},
[76325] = {name = "Призыв пламени", description = "[q]Заклинатель стреляет струями жидкого огня, которые наносят урон от огня на области попадания."},
[76332] = {name = "Искаженное пламя", description = "[q]Наносит 5850 to 6150 ед. урона от огня раз в 1 сек. и вызывает эффект \"Пылающая порча\"."},
[76335] = {name = "Пылающая порча", description = "[q]Увеличивает получаемый урон от огня на 10% на 10 sec. Суммируется до 10 раз."},
[76339] = {name = "Непрекращающаяся агония", description = "[q]Причиняет мучительную боль всем ближайшим противникам, нанося 4750 to 5250 ед. урона от темной магии раз в секунду в течение 10 sec."},
[76340] = {name = "Искаженные чары", description = "[q]Увеличивают скорость произнесения заклинаний и наносимый магический урон на 3%."},
[76355] = {name = "Благословение Солнца", description = "[q]Обжигающий жар солнца наносит 4162 to 4837 ед. урона раз в секунду и увеличивает наносимый урон на 100%."},
[76363] = {name = "Волна порчи", description = "[q]Наносит 1000 ед. урона от сил природы раз в секунду всем противникам в радиусе 4 м в течение 8 sec."},
[76369] = {name = "Сумеречная стрела", description = "[q]Наносит 24375 to 25625 ед. урона от темной магии."},
[76370] = {name = "Искривленные сумерки", description = "[q]Наносит урон от темной магии, равный 50% максимального запаса здоровья цели."},
[76378] = {name = "Чародейский настой", description = "[q]Атаки превращаются в рассекающие удары, наносящие 100% урона от тайной магии."},
[76380] = {name = "Вспышка магмы", description = "[q]Противнику наносится огромный урон от огня."},
[76394] = {name = "Проклятие лазури", description = "[q]Получаемый магический урон увеличен на 50% на 10 sec."},
[76395] = {name = "Холод Ледяной короны", description = "[q]Пронизывается силой Ледяной Короны, снижая получаемый урон от огня на 50%, увеличивая количество получаемого лечения на 1000% и нанося Рагнаросу урон от магии льда раз в секунду."},
[76404] = {name = "Кармазинный заряд", description = nil},
[76409] = {name = "Багровая взрывная волна", description = "[q]Наносит противникам, находящимся в пределах 5 м, 24862 to 26137 ед. урона от огня и отбрасывает их назад."},
[76411] = {name = "Мясорубка", description = "[q]Сумеречный головорез кружится в вихре, нанося 9750 to 10250 ед. физического урона противникам в радиусе 5 м раз в 0.5 сек. в течение 4 sec."},
[76415] = {name = "Одурманивание", description = "[q]Закончив вращение, сумеречный головорез испытывает головокружение. При этом он оглушен и получает на 50% больше урона в течение 4 sec."},
[76416] = {name = "Стрела Тьмы", description = "[q]Наносит 15600 to 16400 ед. урона от темной магии."},
[76418] = {name = "Призыв охотника Скверны", description = "[q]Вызов Охотника Скверны, который будет помогать заклинателю до тех пор, пока не будет освобожден."},
[76423] = {name = "Гнев таунка", description = "[q]Кровавая ярость увеличивает урон и размер, но снижает скорость."},
[76429] = {name = "Статический разряд", description = "[q]Создание электрической волны, которая наносит 20 ед. урона от сил природы. Наносит тройной урон целям, находящимся под водой."},
[76430] = {name = "Огненное цунами", description = "[q]Наносит урон от огня ближайшим противникам и покрывает их лавой, замедляющей их передвижение и наносящей периодический урон от огня в течение 10 sec."},
[76460] = {name = "Резкий старт", description = "[q]Повышает скорость полета на 5 секунд."},
[76473] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 8 sec."},
[76478] = {name = "Раскаленная кочерга", description = "[q]Наносит противнику 6000 to 10000 ед. урона от огня и увеличивает получаемый им в течение 12 sec. урон от огня на 2250 ед."},
[76484] = {name = "Оковы", description = "[q]Сковывает противника на 8 sec. Будучи скован, противник не может передвигаться, атаковать или произносить заклинания. Любой нанесенный урон освободит цель. Единовременно может быть скована только одна цель."},
[76487] = {name = "Дикая бойня", description = "[q]Повышает скорость атаки на 200%, но снижает шанс попадания по цели на 50%. Время действия – 6 sec."},
[76490] = {name = "Шквал когтей", description = "[q]Гризвольд обрушивает свой гнев на ближайших противников, яростно атакуя их в течение 6 sec."},
[76500] = {name = "Коварный удар", description = "[q]Наносит противнику обычный урон, усиленный на 7500 ед."},
[76502] = {name = "Пронзание сердца", description = "[q]Поражает противника кинжалом в самое сердце, нанося 4875 to 5125 ед. физического урона. Если противник имеет более 75% запаса здоровья, наносится тройной урон."},
[76507] = {name = "Укол когтем", description = "[q]Наносит 975 to 1025 ед. физического урона раз в 3 сек. в течение 6 sec. Эффект может суммироваться."},
[76508] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 3 sec."},
[76509] = {name = "Кольцо льда", description = "[q]Наносит ближайшим противникам 1850 to 2150 ед. урона от магии льда, обездвиживая их на 6 sec."},
[76512] = {name = "Зараженная рана", description = "[q]Увеличение физического урона, получаемого противником, на 30 sec."},
[76514] = {name = "Смешанное пламя", description = "[q]Наносит цели 8775 to 9225 ед. урона от огня раз в 1 сек. и оглушает ее на 5 sec."},
[76516] = {name = "Отравленный дротик", description = "[q]Бросок в цель дротика, смазанного смертельным ядом, который мгновенно наносит 19000 to 21000 ед. урона от сил природы и дополнительно 2000 ед. урона от сил природы раз в секунду в течение 10 sec."},
[76517] = {name = "Извергающееся пламя", description = "[q]Пламя извергается из-под ног цели, нанося ей 16087 to 16912 ед. урона от огня и подбрасывая ее в воздух."},
[76520] = {name = "Удар в спину", description = "[q]Наносит 150% урона от оружия противнику, но только при атаке сзади."},
[76522] = {name = "Взрыв Силы", description = "[q]Наносит противнику физический урон, отбрасывая его назад."},
[76524] = {name = "Ужасающий вихрь", description = "[q]Жестокая атака, вызывающая у жертвы кровотечение, требующее полного исцеления. Пока здоровье жертвы не будет полностью восстановление, кровотечение наносит 1500 ед. урона раз в 3 сек."},
[76535] = {name = "Водяная стрела", description = "[q]Наносит 18850 to 21150 ед. урона от магии льда и снижает скорость цели на 5%."},
[76557] = {name = "Ветроступ", description = "[q]Перерожденный ветроступ начинает двигаться так быстро, что его очертания смазываются. При этом он наносит 24375 to 25625 ед. урона находящимся рядом противникам."},
[76560] = {name = "Цунами", description = "[q]Наносит 28275 to 31725 ед. урона от магии льда всем противникам в радиусе 30 м. перед заклинателем и отбрасывает их назад."},
[76561] = {name = "Гравитационный удар", description = "[q]Наносит урон, равный 60% от текущего запаса здоровья противника, и снижает силу атаки. Этот удар невозможно заблокировать или парировать, равно как и уклониться от него."},
[76565] = {name = "Бросок железной руды", description = "[q]Бросок куска лазурной железной породы."},
[76572] = {name = "Быстрый бросок", description = "[q]Бросает оружие в противника, нанося физический урон."},
[76575] = {name = "Кровь ревнителя", description = "[q]Кровь преобразившегося сумеречного ревнителя покрывает ваше оружие, после чего оно начинает наносить увеличенный физический урон от кровотечения."},
[76576] = {name = "Clear Summoned Ore", description = nil},
[76578] = {name = "Цепная молния", description = "[q]Последовательно наносит урон от сил природы цели и находящимся рядом с ней противникам. Поражает до 3 целей:целей."},
[76579] = {name = "Перезарядка", description = "[q]Поражает противника молнией. Наносит 3900 to 4100 ед. урона от сил природы раз в 3 сек. и усиливает атаки электрическими разрядами. Урон от разрядов увеличивается при суммировании эффекта \"Перезарядка\"."},
[76582] = {name = "Парализующий удар", description = "[q]Нанесение противнику 10 ед. урона и парализация его до 4 sec. Вы автоматически прекращаете атаковать цель. Цель должна быть повернута к вам лицом. Любой полученный урон оживит цель."},
[76583] = {name = "Пинок", description = "[q]Нанесение противнику урона и прерывание произнесения заклинания на 6 sec."},
[76584] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[76588] = {name = "Кольцо Тьмы", description = "[q]Наносит 7400 to 8600 ед. урона от темной магии всем ближайшим противникам."},
[76589] = {name = "Чародейский обстрел", description = "[q]Запуск в противника волшебных стрел, наносящих 5400 to 6600 ед. урона от тайной магии."},
[76590] = {name = "Удары по земле", description = "[q]Серия периодических ударов, вызывающих землетрясение и наносящих 24000 ед. урона раз в 0.5 сек. всем противникам в радиусе 5 м в течение 6 sec."},
[76591] = {name = "Аура чародейского ускорения", description = "[q]Повышает вашу скорость произнесения заклинаний на 20%. На 2 min. увеличивает урон от заклинаний на 1500 ед."},
[76592] = {name = "Чародейское ускорение", description = "[q]Повышает скорость произнесения заклинаний ближайших союзников на 20%. На 5 sec. увеличивает урон от заклинаний на 1500 ед."},
[76594] = {name = "Кровопускание", description = "[q]Нанесение противнику физического урона раз в 3 сек. в течение 15 sec."},
[76596] = {name = "Каменная кожа", description = "[q]Кожа цели твердеет, получаемый ею урон уменьшается на 75%."},
