[76610] = {name = "Земной шок", description = "[q]Мгновенный шокирующий удар, наносящий 7800 to 8200 ед. урона от сил природы."},
[76612] = {name = "Чародейские стрелы", description = "[q]Выпускает в противника магическую стрелу, наносящую урон от тайной магии."},
[76617] = {name = "Тепловой удар", description = "[q]Невыносимый жар изнуряет противников на большой области, снижая максимальный запас их здоровья на 10%, а скорость передвижения на 10%. Эффект суммируется."},
