[76665] = {name = "Маленькое \"большое огненное дыхание\"", description = "[q]Наносит 49900 ед. урона от огня в течение 5 sec."},
[76679] = {name = "Обезоруживающий жар", description = "[q]Обезоруживает цель и усиливает ее атаки ближнего боя дополнительным уроном от огня."},
[76685] = {name = "Усиливающий сумрак", description = "[q]Вызов круглой области усиливающего сумрака под багровым полководцем. Находясь в этой области, полководец наносит на 50% больше урона."},
[76686] = {name = "Клетка Тьмы", description = "[q]Заключает противника. Если противник двигается, наносит ему урон от темной магии."},
