[78144] = {name = "Удар бури", description = "[q]Мгновенная атака обоими оружиями. 4 последующие атаки:последующих атак от сил природы нанесут цели на 20% больше урона. Время действия – 12 sec."},
[78146] = {name = "Вскипание лавы", description = "[q]Вы заряжаете оружие в левой руке энергией лавы и мгновенно наносите противнику урон в размере 100% от урона этого оружия. Если это оружие зачаровано \"Языком пламени\", наносимый урон увеличивается на 25%."},
[78147] = {name = "Неистовство ветра", description = "[q]Вас подхватывает неистовый ветер и позволяет наносить 2 дополнительных удара:дополнительных ударов."},
[78152] = {name = "Оружие Водоворота", description = "[q]Уменьшает время произнесения следующего заклинания \"Молния\", \"Цепная молния\", \"Малая волна исцеления\", \"Волна исцеления\" и \"Цепное исцеление\" на 20%."},
[78154] = {name = "Жажда крови", description = "[q]Увеличение скорости ближнего и дальнего боя, а также произнесения заклинаний на 30% для всех участников группы. Время действия – 40 sec."},
[78156] = {name = "Тотем трепета", description = "[q]Заклинатель создает рядом с собой тотем трепета прочностью 5 ед., который сотрясает землю, снимая эффекты страха, подчинения и сна с участников группы в радиусе 30 м. Время действия – 5 min."},
[78159] = {name = "Тотем неистовства ветра", description = "[q]Заклинатель создает рядом с собой тотем неистовства ветра прочностью 5 ед. Тотем повышает скорость ближнего боя участников группы или рейда в радиусе 40 м на 10%. Время существования тотема – 5 min."},
[78160] = {name = "Сумеречный разлом", description = "[q]Наносит урон от темной магии всем оказавшимся в темном разломе противникам по истечении 5 сек."},
[78162] = {name = "Взрыв Бездны", description = "[q]Наносит урон от темной магии всем оказавшимся в темном разломе противникам по истечении 5 сек."},
