[78509] = {name = "Бросок факела", description = "[q]Бросает факел, наносящий урон от огня и поджигающий противника, заставляя его получать дополнительный урон от огня в течение 10 sec."},
[78515] = {name = "Песчаный вихрь", description = nil},
[78521] = {name = "Дыхание дракона", description = "[q]Противники, находящиеся в конусе поражения перед заклинателем, получают урон от огня и оказываются дезориентированы на 1.50 sec. Любой нанесенный прямой урон снимет этот эффект."},
