[78646] = {name = "Костяной щит", description = "[q]Поглощает урон.\n Наносит 13 to 16 ед. урона атакующим в ближнем и дальнем бою.\n Наносит 13 to 16 ед. урона заклинателям."},
[78647] = {name = "Призыв скелета", description = "[q]Призыв 1 скелета для сопровождения заклинателя, пока он не разрешит удалиться."},
