[78939] = {name = "Элементиевая защита", description = "[q]Зарядившись, защищающие Озрука элементиевые пластины с вероятностью 100% в течение 10 sec. отражают направленные на него заклинания."},
[78941] = {name = "Заражение паразитами", description = "[q]Наносит 23125 to 26875 ед. урона раз в 2 сек. Лавовые паразиты вгрызаются в тело и размножаются в его прохладных внутренностях."},
