[79086] = {name = "Цепная молния", description = "[q]Последовательно наносит урон от сил природы цели и находящимся рядом с ней противникам. Поражает до 3 целей:целей."},
[79094] = {name = "Похищение души", description = "[q]Похищение души противника и нанесение ему урона от темной магии раз в 1 сек. в течение 5 sec."},
[79109] = {name = "Песчаный вихрь", description = nil},
[79115] = {name = "Древняя тьма", description = "[q]Выстреливает в противника стрелу тьмы, наносящую урон от темной магии."},
