[79130] = {name = "Глубокая заморозка", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 1 min."},
[79174] = {name = "Струя песка", description = "[q]Наносит урон противникам, находящимся в конусе поражения перед заклинателем, снижая скорость их передвижения и увеличивая время между их атаками на 3 sec."},
