[79203] = {name = "Тушение огня", description = "[q]Марион Червивое Крыло чует неладное."},
[79213] = {name = "Поглощение сущности", description = "[q]Поглощает ближайшего безликого надзирателя, убивая того и передавая 200% от запаса здоровья последнего заклинателю."},
[79222] = {name = "Водяной взрыв", description = "[q]Насылает на Халлазила и его воплощенных фанатиков разрушительный водяной взрыв, наносящий 18000 to 22000 ед. урона."},
