[79339] = {name = "Взрывчатая субстанция", description = "[q]Поджигает несколько случайных целей, покрывая их взрывчатой субстанцией, который периодически наносит урон от огня. Субстанция взрывается через 8 sec., нанося 42750 to 47250 ед. урона от огня в небольшой области."},
[79340] = {name = "Призыв гравитационного колодца", description = "[q]Создает область повышенной гравитации. Любой противник или союзник в радиусе 10 м от этой области будет притянут к ее центру. Заклинание постоянно наносит урон всем противникам в зоне действия. Урон увеличивается по мере приближения к центру области. Время действия – 20 сек."},
[79345] = {name = "Проклятие крови", description = "[q]Увеличивает получаемый целью физический урон на 5000 на 15 sec."},
[79351] = {name = "Захват силы", description = "[q]Окружает цель энергетическим полем, поднимая ее в воздух, а затем швыряя оземь. При каждом ударе о землю цель получает 70687 to 79312 ед. урона. Наносимый урон не уменьшается за счет брони. Время действия – 5 sec."},
[79353] = {name = "Тень малодушия", description = "[q]Наносит 30000 ед. урона от темного пламени и увеличивает получаемый урон от темной магии на 100%."},
[79356] = {name = "Осклизлая кожа", description = "[q]Покрывает цель слизью, позволяющей быстрее плавать, но наносящей урон от сил природы раз в секунду."},
[79379] = {name = "Удар пушечным ядром", description = "[q]Наносит 300% урона от оружия."},
[79384] = {name = "Натиск моря", description = "[q]Наносит 300% урона от оружия и заряжает находящихся рядом противников энергией."},
[79400] = {name = "Яд солончаковой змеи", description = "[q]Снижает силу и интеллект на 700."},
[79403] = {name = "Шпион", description = "[q]Заклинатель становится незаметным, но скорость его передвижения снижается до 50% от обычной. Длится, пока не будет отменено."},
