[79490] = {name = "Зараженный укус", description = "[q]Увеличение времени между атаками противника на 54%, снижение скорости его передвижения на 25% на 10 sec."},
[79501] = {name = "Выбор цели", description = "[q]Выбор цели. Спустя 4 sec. магматрон начинает палить из огнемета по цели, нанося всем находящимся перед ним противникам 63375 to 66625 ед. урона от огня раз в 1 сек. в течение 4 sec."},
[79503] = {name = "Шквал", description = "[q]Уменьшает наносимый заклинателем физический урон на 50. Скорость атаки в ближнем бою увеличивается на 200%."},
