[79537] = {name = "Удушение веревкой", description = "[q]Душит противника веревкой. Противник теряет 10% здоровья раз в 2 сек. в течение 6 sec."},
[79539] = {name = "Сокрушительная волна", description = "[q]Неистовый поток обрушивается на противников, нанося им урон от магии льда и отбрасывая их на 5 м назад."},
