[79559] = {name = "Светящаяся чешуя", description = "[q]Снижает вероятность уклонения противника на и противника на 20%, а ловкость – на 10% на 1 min. Под действием этого заклинания цель не может перейти в состояние незаметности или невидимости."},
[79560] = {name = "Дробящий укус", description = "[q]Раскалывает доспехи противника, ослабляя броню за каждый наложенный эффект \"Раскол брони\". Суммируется до 5 раз. Время действия – 20 sec."},
[79561] = {name = "Мания крови", description = "[q]Вкус крови приводит заклинателя в исступление, увеличивая наносимый урон на 5% на 30 sec."},
[79563] = {name = "Ледяной доспех", description = "[q]Увеличивает показатель брони на 2000 и сопротивление магии льда на 50. Когда противник наносит заклинателю удар, скорость его передвижения снижается на 30%, а время между атаками увеличивается на 20% на 5 sec. Маг может использовать единовременно только одно заклинание типа \"Доспех\". Время действия – 30 min."},
[79564] = {name = "Сумеречный огненный шар", description = "[q]Наносит противнику урон от огня."},
[79565] = {name = "Сумеречная ударная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 6 sec."},
[79566] = {name = "Водные струи", description = "[q]Разбрызгивание воды во всех направлениях с нанесением противникам урона от магии льда и отбрасыванием их назад."},
[79571] = {name = "Конус холода", description = "[q]Нанесение противникам перед заклинателем урона от магии льда и снижение их скорости передвижения на 4 sec."},
[79580] = {name = "Удар с размаха", description = "[q]Наносит 8750 to 11250 ед. урона всем противникам в 20-метровом конусе поражения перед заклинателем, также отбрасывая их назад."},
