[79582] = {name = "Преграда", description = "[q]Поглощает 450000 ед. урона. При уничтожении преграды возникает огненная буря, наносящая всем противникам 151125 to 158875 ед. урона от огня."},
[79583] = {name = "Притягивающий трезубец", description = "[q]Бросает трезубец в противника и притягивает его к себе, нанося 24 to 29 ед. физического урона и провоцируя его."},
[79584] = {name = "Веер клинков", description = "[q]Бросок ножей, наносящий физический урон всем находящимся рядом противникам."},
[79589] = {name = "Удушающие оковы", description = "[q]Сковывает цели, нанося 18500 to 21500 ед. физического урона раз в 1 с и оглушая цель на 5 sec."},
[79604] = {name = "Удар грома", description = "[q]Наносит 63750 to 86250 ед. урона от сил природы всем противникам в радиусе 15 м и снижает их скорость атаки на 20%."},
[79607] = {name = "Ядовитые брызги", description = "[q]Наносит урон от сил природы всем отравленным ядом противникам. Длительность – 10 sec."},
[79610] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[79615] = {name = "Исчезновение", description = nil},
