[79673] = {name = "Портал Бездны", description = "[q]Создание портала Бездны, который наносит урон от темной магии всем находящимся в ней противникам."},
[79675] = {name = "Битва разумов", description = "[q]Заставляет противника сражаться с самим собой."},
[79687] = {name = "Рассекающий колун", description = "[q]Наносит противнику и его ближайшим союзникам обычный урон, повышенный на 5 ед. Атакует до 3 целей. Удар ослабляет броню на 50 ед."},
[79710] = {name = "Волшебный уничтожитель", description = "[q]Наносит 78000 to 82000 ед. урона от тайной магии случайно выбранному противнику."},
[79719] = {name = "Засечь цель", description = "[q]Увеличивает силу атаки дальнего боя всех атакующих отмеченную цель на 20. Цель всегда видна стрелкам, даже если крадется или находится под покровом невидимости. Длительность – 30 sec."},
