[79797] = {name = "Гром и молния", description = "[q]Удар молнии наносит всем противникам в радиусе 15 м 3237 to 3762 ед. урона от сил природы и отбрасывает их назад."},
[79810] = {name = "Морозный удар", description = "[q]Наносит противнику и его ближайшим союзникам урон от оружия, увеличенный на 110%, и снижает их скорость атаки и произнесения заклинаний на 10%. Захватывает до 3 целей."},
[79811] = {name = "Слияние с Тьмой", description = "[q]Вы растворяетесь во Тьме. Весь получаемый вами урон уменьшается на 90%. Во время действия этого заклинания вы не можете атаковать или читать заклинания, но восполняете 6% маны раз в секунду. Время действия – 6 sec. Можно использовать, даже если на вас наложены эффекты оглушения, страха или немоты. При использовании рассеивает сковывающие и затрудняющие движение эффекты, а также дает невосприимчивость к ним на время действия заклинания."},
[79821] = {name = "Зул'гурубское внутреннее зрение", description = "[q]Позволяет вам смотреть глазами пойманного детеныша кнутохвоста."},
[79822] = {name = "Кольцо света", description = "[q]Создает вокруг заклинателя вспышку священного света, наносящую 24 to 29 ед. урона от светлой магии всем целям противника в радиусе 10 м и исцеляющую всех участников группы в радиусе 10 метров:метров на 360 to 440 ед. Эти эффекты не увеличивают угрозы."},
[79823] = {name = "Звездопад", description = "[q]Нанесение урона от тайной магии противникам в указанной области раз в 2 сек. в течение 10 sec."},
[79824] = {name = "Призыв древня-союзника", description = "[q]Призыв 1 древня-союзника для сопровождения заклинателя, пока он не разрешит им удалиться."},
[79825] = {name = "Вызов изменчивого гриба", description = "[q]Вызов гнилого гриба."},
[79826] = {name = "Нестабильное облако", description = "[q]Наносит урон от сил природы в течение 15 sec.\n Снижает скорость передвижения на 25%."},
[79828] = {name = "Увечье", description = "[q]Наносит противнику 24 to 29 ед. урона и дополнительно 20 ед. урона в течение 15 sec."},
[79831] = {name = "Девятый вал", description = "[q]Наносит урон от магии льда."},
[79833] = {name = "Знак дикой природы", description = "[q]Повышает силу, ловкость, выносливость и интеллект дружественной цели на 5% на 1 hour. Если цель находится вместе с вами в группе или рейде, эффект будет наложен на всех участников."},
[79835] = {name = "Ядовитые доспехи", description = "[q]Атакуя в ближнем бою и заклинаниями, противники могут оказаться под ядовитым ливнем, наносящим 20000 ед. урона от сил природы раз в 2 сек. Эффект суммируется."},
[79840] = {name = "Закалка", description = "[q]Оболочка заклинателя усиливается хитином, за счет чего весь получаемый урон снижается на 5% на 20 sec."},
[79846] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке и наносит дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[79847] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 6 sec."},
[79848] = {name = "Раскаленный доспех", description = "[q]Наносит урон от огня."},
[79849] = {name = "Раскаленный доспех", description = "[q]Наносит урон от огня при попадании, повышает вероятность нанесения критического удара заклинаниями на 3% и снижает вероятность получить критический удар на 5%. Маг может использовать единовременно только одно заклинание типа \"Доспех\". Время действия – 10 min."},
[79850] = {name = "Кольцо льда", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 4 sec."},
[79851] = {name = "Веер клинков", description = "[q]Мгновенный бросок ножей во всех находящихся рядом противников, наносящий 100% урона оружия."},
[79852] = {name = "Потрошение", description = "[q]Завершающий удар, наносящий урон."},
[79853] = {name = "Ускользание", description = "[q]Повышает вероятность уклонения от удара на 35%."},
[79854] = {name = "Огненный шар", description = "[q]Наносит противнику урон от огня."},
[79855] = {name = "Огненный взрыв", description = "[q]Наносит противнику урон от огня."},
[79856] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке и наносит дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[79857] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 6 sec."},
[79858] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[79859] = {name = "Ледяное копье", description = "[q]Наносит урон от магии льда. Замороженным целям наносится двойной урон."},
[79860] = {name = "Снежная буря", description = "[q]Начинается снежная буря, длящаяся 8 sec. и наносящая урон от магии льда раз в 2 сек. всем противникам в зоне поражения."},
[79862] = {name = "Удар в спину", description = "[q]Наносит 150% урона противнику при атаке со спины."},
[79863] = {name = "Кровоизлияние", description = "[q]Нанесение урона от оружия и увеличивает получаемый противником физический урон на 20% на 10 sec."},
[79864] = {name = "Шаг сквозь тень", description = "[q]Пройти сквозь тень, чтобы появиться за спиной у противника."},
[79865] = {name = "Морозный доспех", description = "[q]Обволакивает заклинателя толстым слоем льда на 30 min. Лед усиливает броню, уменьшает скорость передвижения противников, атакующих в ближнем бою, и увеличивает время между их атаками."},
[79866] = {name = "Смертельный бросок", description = "[q]Бросает кинжал в противника, нанося 24 to 29%-й урон от оружия. Кроме того, прерывает процесс произнесения заклинаний этой школы на 3 sec."},
[79868] = {name = "Чародейская вспышка", description = "[q]Поражает противника зарядом энергии, нанося ему урон от тайной магии. Каждый раз, когда вы применяете заклинание \"Чародейская вспышка\", урон, наносимый любыми заклинаниями тайной магии, увеличивается на 10%, при этом расход маны на заклинание \"Чародейская вспышка\" увеличивается на 150%. Эффект суммируется до 4 раз и продолжается 6 sec. или до тех пор, пока не будет произнесено любое другое заклинание тайной магии."},
[79870] = {name = "Звериный рывок", description = "[q]Стремительный рывок к противнику, который обездвиживает его и прерывает произнесение заклинаний на until cancelled."},
[79871] = {name = "Безрассудство", description = "[q]Невосприимчивость к эффектам страха.\n Все атаки наносят критический урон.\n Броня ослаблена на 35%."},
[79872] = {name = "Ударная волна", description = "[q]Посылает перед воином силовую волну, наносящую физический урон всем противникам в конусе поражения радиусом 10 м и оглушающую их на 4 sec."},
[79875] = {name = "Удар Онголонго!", description = "[q]Отбрасывает противника назад."},
[79877] = {name = "Кровожадность", description = "[q]Мгновенная атака, наносящая противнику 120% обычного урона от оружия. Последующие успешные атаки в ближнем бою восстанавливают 1% максимального запаса здоровья. Время действия – 10 sec."},
[79878] = {name = "Кровожадность", description = "[q]Мгновенная атака, наносящая противнику 120% обычного урона от оружия. Последующие успешные атаки в ближнем бою восстанавливают 1% максимального запаса здоровья. Время действия – 10 sec."},
[79879] = {name = "Электроразряд", description = "[q]Наносит 43875 to 46125 ед. урона от сил природы максимум 3 противникам в радиусе 8 м."},
[79880] = {name = "Замедление", description = "[q]Увеличивает время между атаками противника на 25%, время произнесения заклинаний – на 25% и снижает скорость передвижения на 25% на 6 sec."},
[79881] = {name = "Мощный удар", description = "[q]Сокрушительный удар, наносящий противнику урон от оружия и дополнительный урон."},
[79883] = {name = "Сокрушительный бросок", description = "[q]Бросает в противника оружие, нанося урон от оружия и ослабляя броню цели на 20% на 10 sec."},
[79884] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[79885] = {name = "Удар в сердце", description = "[q]Стремительный удар, наносящий основной цели 110% урона от оружия, второстепенной – 55% урона от оружия."},
[79886] = {name = "Выброс лавы", description = "[q]Вы бросаете в противника раскаленную лаву, нанося урон от огня."},
[79887] = {name = "Смерть и разложение", description = "[q]Раз в 1 сек. всем целям в области действия заклинания наносится урон от темной магии. Время действия – 10 sec."},
[79888] = {name = "Проводник молний", description = "[q]Заражает противника на 15 sec. Наносит урон от сил природы его ближайшим союзникам раз в 2 сек."},
[79890] = {name = "Огненный шок", description = "[q]Поджигает противника, а затем наносит дополнительный урон от огня раз в 3 сек. в течение 12 sec."},
[79891] = {name = "Зимний горн", description = "[q]Рыцарь смерти трубит в зимний горн, накапливая 10 ед. силы рун и повышая силу и ловкость участников группы или рейда на 5. Время действия – 2 min."},
[79892] = {name = "Водный щит", description = "[q]Заклинателя окружают 3 водных сферы:водных сфер. Атаки и заклинания, направленные против заклинателя, будут наносить атакующему урон от магии льда и уменьшать скорость произнесения заклинаний. Время действия – 10 min. Количество зарядов – 3."},
[79893] = {name = "Кровавый червь", description = "[q]Создание кровавого червя, который атакует ближайшую цель. Время действия – 8 sec."},
[79894] = {name = "Хватка смерти", description = "[q]Используя нечестивую энергию, пронизывающую и объединяющую все сущее, притягивает противника к рыцарю смерти, заставляя его атаковать рыцаря в течение 3 sec."},
[79895] = {name = "Ледяной удар", description = "[q]Мгновенная атака противника, наносящая ему урон от магии льда, равный 110% урона от оружия."},
[79896] = {name = "Воющий ветер", description = "[q]Наносит урон от магии льда всем противникам в радиусе 10 м от основной цели. Наносит тройной урон замороженным целям."},
[79897] = {name = "Ледяное прикосновение", description = "[q]Наносит урон от магии льда и уменьшает скорость атаки ближнего боя, дальнего боя и произнесения заклинаний на 15% на 8 sec."},
[79899] = {name = "Ледяные оковы", description = "[q]Ледяные цепи сковывают противника, снижая скорость его передвижения до 5% от нормальной. Каждую секунду в течение 10 sec. скорость движения противника восстанавливается на 10%."},
[79900] = {name = "Нестабильный щит", description = "[q]Атаки нестабильного щита могут привести к статическому шоку, поражающему находящихся рядом противников. Статический шок наносит 58500 to 61500 ед. урона от сил природы противникам, находящимся в радиусе 6 м от цели."},
[79901] = {name = "Кровавая чума", description = "[q]Жестокая атака оружием, дополнительно заражающая цель чумой, наносящей урон от темной магии в течение 12 sec."},
[79902] = {name = "Удар чумы", description = "[q]Жестокий удар, наносящий наносит урон от оружия и поражающий цель чумой, нанося 8000 ед. урона от темной магии в течение 12 sec."},
[79903] = {name = "Лик смерти", description = "[q]Наносит 24 to 29 ед. урона от темной магии случайному находящемуся поблизости противнику."},
[79904] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий 13423 to 15600 ед. урона цели и ее ближайшим союзниками и отбрасывающий их назад."},
[79905] = {name = "Воскрешение мертвых", description = "[q]Воскрешает мертвеца, который восстает в виде вурдалака, чтобы сражаться на вашей стороне. Если поблизости нет трупа гуманоида, который принес бы опыт или очки чести, для призыва вурдалака потребуется прах. У вас может быть только 1 вурдалак единовременно. Эффект длится 1 min."},
[79907] = {name = "Зона антимагии", description = "[q]Ограждает от магии большую зону, в которой урон от заклинаний, наносимый участникам группы или рейда, снижается на 75%. Зона антимагии действует в течение 10 sec. или до тех пор, пока не поглотит (10000 + 2 * Attack power) ед. урона от заклинаний."},
[79909] = {name = "Удар Плети", description = "[q]Нечестивый удар, наносящий 150% урона от оружия в виде урона от темной магии."},
[79913] = {name = "Цепная молния", description = "[q]Последовательно наносит урон от сил природы цели и находящимся рядом с ней противникам. Поражает до 3 целей:целей, нанося меньше урона каждой следующей цели."},
[79914] = {name = "Дух дикого волка", description = "[q]Призывает 2 духов волка под командование шамана. Время жизни – 45 sec."},
[79921] = {name = "Темное пламя", description = "[q]Наносит 23125 to 26875 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[79922] = {name = "Обдирание", description = "[q]Наносит 60% урона от оружия всем противникам в радиусе 4 м раз в 0,25 сек. в течение 5 sec."},
[79924] = {name = "Волна исцеления", description = "[q]Призывает силы природы для исцеления союзника."},
[79925] = {name = "Ледяной шок", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 6 sec."},
[79927] = {name = "Щит земли", description = "[q]Защита цели земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд. 10 атаки. Время действия – 10 min."},
[79928] = {name = "Щит земли", description = "[q]Защита цели земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд."},
[79930] = {name = "Порча", description = "[q]Нанесение находящимся рядом противникам урона от темной магии раз в 3 сек. в течение 15 sec."},
[79932] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[79933] = {name = "Похищение жизни", description = "[q]Похищение здоровья противника в течение 5 sec. и передача его заклинателю."},
[79934] = {name = "Демонический доспех", description = "[q]Защита заклинателя, усиливающая его броню, а также увеличивающая количество здоровья, получаемого при помощи заклинаний и эффектов, на 20%. Чернокнижник может находиться под воздействием лишь одного заклинания класса \"Доспех\". Время действия – 30 min."},
[79937] = {name = "Жертвенный огонь", description = "[q]Заклинание обжигает противника и наносит дополнительный урон от огня раз в 3 сек. в течение 15 sec."},
[79938] = {name = "Испепеление", description = "[q]Нанесение вашей цели урона от огня."},
[79939] = {name = "Стрела Хаоса", description = "[q]Выпускает в противника стрелу огня хаоса, наносящую ему урон от огня. Сопротивление стреле хаоса невозможно, и она пробивает защиту от магии."},
[79949] = {name = "Водный щит", description = "[q]Заклинателя окружает щит из 3 водяных сфер:водяных сфер, восполняющий 10 ед. маны раз в 5 секунд. Каждый раз, когда заклинатель получает удар в ближнем, в дальнем бою или заклинанием, щит восполняет 758 ед. маны, теряя одну сферу. Этот эффект срабатывает не чаще чем раз в несколько секунд. Время действия – 10 min. Шаман может быть защищен только одним заклинанием категории \"Щит стихий\" одновременно."},
[79954] = {name = "Доспех Скверны", description = "[q]Окружает заклинателя энергией Скверны, увеличивающей силу заклинаний на 10%. Кроме того, каждые 5 секунд восстанавливается 2% от максимального запаса здоровья. Чернокнижник может находиться под воздействием лишь одного заклинания класса \"Доспех\". Время действия – 30 min."},
[79955] = {name = "Огненный ливень", description = "[q]Призывает раскаленный дождь пролиться, поджигая всех противников на выбранной территории и нанося им урон от огня раз в 3 сек. в течение 9 sec."},
[79956] = {name = "Проклятие стихий", description = "[q]Накладывает на противника проклятие на 30 sec., снижая сопротивлении тайной магии, огню, магии льда, силами природы и темной магии на 45, а также увеличивая получаемый магический урон на 8%. Единовременно может быть наложено только одно проклятие."},
[79960] = {name = "Свет небес", description = "[q]Призывает силы света для исцеления союзника."},
[79961] = {name = "Шок небес", description = "[q]Переполняет противника энергией светлой магии, нанося ему урон."},
[79962] = {name = "Печать праведности", description = "[q]Наполняет заклинателя священным духом на 30 min. С каждой атакой ближнего боя, действующей на одну цель, заклинатель наносит дополнительный урон от светлой магии."},
[79963] = {name = "Аура сосредоточенности", description = "[q]Союзники в пределах 40 м теряют на 35% меньше времени произнесения заклинаний или поддержания эффектов при получении урона."},
[79964] = {name = "Экзорцизм", description = "[q]Нанесение противнику урона от светлой магии."},
[79965] = {name = "Щит мстителя", description = "[q]Бросок в противника священным щитом, наносящий урон от светлой магии и вызывающий головокружение. Затем щит перескакивает на других находящихся поблизости противников. Способен воздействовать на 2 цели:целей."},
[79966] = {name = "Освящение", description = "[q]Освящает участок земли, на котором стоит заклинатель, нанося урон от светлой магии в течение 8 sec. всем противникам, ступившим на освященную землю."},
[79967] = {name = "Щит небес", description = "[q]Повышает вероятность блокировать удар на 30% на 10 sec. Если атака блокирована, атакующему наносится урон от светлой магии. При каждом блоке расходуется 1 заряд. 3 заряда:зарядов."},
[79968] = {name = "Благословение королей", description = "[q]Благословляет дружественную цель, на 5 min. повышая ее силу, ловкость, выносливость и интеллект на 5%."},
[79970] = {name = "Божественная буря", description = "[q]Мгновенная атака оружием, наносящая урон от светлой магии 4 противникам:противникам в радиусе 8 м."},
[79971] = {name = "Молот гнева", description = "[q]Бросает в противника молот, наносящий урон от светлой магии."},
[79972] = {name = "Печать правды", description = "[q]Наполняет заклинателя священной силой, позволяющей при атаке по одиночной цели наложить эффект \"Порицание\", который наносит [(0.01 * Spell power + 0.0193 * Attack power) * 5 */ 100] ед. дополнительного урона от светлой магии в течение 15 sec. Эффект суммируется до 5 раз. При 5-кратном суммировании эффекта каждая атака паладина начинает наносить дополнительно 15% урона от оружия в виде урона от светлой магии. Единовременно паладин может использовать только одно заклинание категории \"Печать\". Время действия – 5 min.\n\n Высвободив энергию печати, вы наносите противнику (1 + 0.223 * Spell power + 0.142 * Attack power) ед. урона от светлой магии. Этот урон увеличивается на 10% за каждый наложенный на цель эффект \"Порицание\"."},
[79975] = {name = "Зул'гурубский контроль над разумом", description = "[q]Дает вам контроль над пойманным детенышем кнутохвоста."},
[79976] = {name = "Аура воздаяния", description = "[q]Наносит урон от светлой магии любому существу, атакующему заклинателя."},
[79977] = {name = "Благословение могущества", description = "[q]Благословляет дружественную цель, увеличивая силу атаки на 20%, силу атаки дальнего боя на 10% и восстанавливая 1 маны раз в 5 сек. Эффект длится 5 min."},
[80003] = {name = "Черная стрела", description = "[q]Черная стрела поражает цель, повышая весь наносимый вами урон по цели на 6% и нанося урон от темной магии в течение 15 sec."},
[80004] = {name = "Змеиная ловушка", description = "[q]Установка ловушки, из которой выползают несколько ядовитых змей, атакуя первого приблизившегося противника. Змеи умирают по прошествии 5 sec. Время существования ловушки – 1 min."},
[80009] = {name = "Укус змеи", description = "[q]Жалит противника, нанося урон от сил природы в течение 15 sec."},
[80011] = {name = "Облить ядом", description = "[q]Искупавшись в яде, вы получаете 20000 ед. урона от сил природы раз в 2 сек., а ваши заклинания и способности наносят противнику 19500 to 20500 ед. урона от сил природы."},
[80015] = {name = "Верный выстрел", description = "[q]Верный выстрел, наносящий противнику увеличенный на (Ranged attack power * 0.1 + 0) ед. урон от оружия."},
[80016] = {name = "Метка охотника", description = "[q]Сила всех атак дальнего боя, направленных против отмеченной охотником цели, повышается на 10%. Кроме того, охотник всегда видит отмеченного противника, даже если тот находится в режиме незаметности или невидим, и может следить за ним по миникарте. Эффект длится 30 sec."},
[80018] = {name = "Ледяная ловушка", description = "[q]Установить ледяную ловушку, которая создает вокруг себя толстый слой льда на 30 sec, когда к ней приближается первый противник. Все противники в радиусе 10 м замедляются на 50%, пока остаются в зоне действия эффекта. Ловушка существует 30 sec. Можно создавать только одну ловушку одновременно."},
[80019] = {name = "Аура ледяной ловушки", description = "[q]Установить ледяную ловушку, которая создает вокруг себя толстый слой льда на 30 sec., когда к ней приближается первый противник. Все противники в радиусе 10 м замедляются на 50%, пока остаются в зоне действия эффекта."},
[80021] = {name = "Сеть", description = "[q]Обездвиживание противника на 4 sec."},
[80022] = {name = "Прогрызание сети", description = "[q]Прогрызть сети троллей Гурубаши. Железнотканую сеть прогрызть невозможно."},
[80028] = {name = "Каменный бур", description = "[q]Наносит 4000 ед. урона раз в 3 сек. Суммируется до 10 раз. Время действия – 15 sec."},
[80031] = {name = "Пылающий разрез", description = "[q]Нанесение 110% от обычного урона ближнего боя противнику и его ближайшему союзнику."},
[80035] = {name = "Ярость мести", description = "[q]Смерть товарища порождает волну негодования, которая повышает наносимый урон на 100%, а скорость атаки на 50%."},
[80038] = {name = "Извержение магмы", description = "[q]Разбрасывает во все стороны раскаленные камни, нанося 56550 to 63450 ед. урона от огня всем противникам в радиусе 2 м от места падения."},
[80051] = {name = "Тяжкая рана", description = "[q]Жестокая атака, наносящая противнику кровоточащую рану, которая затягивается только при исцелении 90% от общего объема здоровья."},
[80053] = {name = "Протокол отравления", description = "[q]Согласно протоколу отравления раз в 3 сек. создается ядовитая бомба, которая взрывается при уничтожении или при контакте с целью."},
[80058] = {name = "Сумеречная вспышка", description = "[q]Сумеречный взрыв наносит урон от темной магии."},
