[81420] = {name = "Луч суперуменьшения", description = "[q]Мощный энергетический луч поражает противника, снижая его силу атаки на 30% на 10 sec."},
[81426] = {name = "Обнаружение незаметности", description = "[q]Обнаруживает незаметных существ."},
[81427] = {name = "Риторика", description = "[q]Сбивает противника с толку, засыпав его словами. Получаемый урон увеличивается на 25% на 40 sec."},
