[81440] = {name = "Залп ледяных стрел", description = "[q]Наносит 17575 to 19425 ед. урона от магии льда всем противникам в радиусе 40 м и снижает скорость их передвижения на 30% на 2 sec."},
[81441] = {name = "Неистовство Тьмы", description = "[q]Наносит 14250 to 15750 ед. урона от темной магии и оглушает всех противников в радиусе 10 м от случайной цели."},
[81442] = {name = "Страх", description = "[q]Вселение ужаса в противника, заставляющего его бежать в течение 4 sec. Количество одновременно напуганных противников – 1."},
[81452] = {name = "Свет небес", description = "[q]Призывает силы света для исцеления союзника."},
[81453] = {name = "Шок небес", description = "[q]Переполняет противника энергией светлой магии, нанося ему урон."},
[81454] = {name = "Печать праведности", description = "[q]Наполняет заклинателя священным духом на 30 min. С каждой атакой ближнего боя, действующей на одну цель, заклинатель наносит дополнительный урон от светлой магии."},
[81455] = {name = "Аура сосредоточенности", description = "[q]Союзники в пределах 40 м теряют на 35% меньше времени произнесения заклинаний или поддержания эффектов при получении урона."},
[81456] = {name = "Экзорцизм", description = "[q]Нанесение урона от светлой магии нежити."},
[81459] = {name = "Сила земли", description = "[q]Переходит в силу земли, увеличивая наносимый урон и давая новые возможности."},
[81460] = {name = "Свирепый удар", description = "[q]Освящение оружия заклинания с нанесением дополнительного урона при следующей атаке. Весь нанесенный урон основан на школе Света."},
[81463] = {name = "Пыльный ураган", description = "[q]Поднимает сильный ураган, наносящий 4712 to 5287 ед. урона находящимся поблизости противникам раз в 0.5 сек. Время действия – 6 sec."},
[81500] = {name = "Громовая поступь", description = "[q]Наносит физический урон и оглушает до 5 противников:противников в радиусе 8 м на 2 sec."},
[81502] = {name = "Рассекающий удар", description = "[q]Наносит 110% обычного урона в ближнем бою всем находящимся рядом противникам."},
[81505] = {name = "Запуск крошшера поддержки", description = "[q]Вызов крошшера орбитальной поддержки на помощь заклинателю в течение 30 sec."},
[81506] = {name = "Огонь!", description = "[q]Палите по цели!"},
