[81521] = {name = "Огненный шар", description = "[q]Наносит противнику урон от огня."},
[81529] = {name = "Ракетный обстрел", description = "[q]Классное зрелище, детка. Просто классное."},
[81530] = {name = "Подземный толчок", description = "[q]Воздействует на область перед заклинателем, нанося 37700 to 42300 ед. урона от сил природы всем противникам в радиусе 2 м и подбрасывая их в воздух."},
[81541] = {name = "Огонь!", description = "[q]Палите по цели!"},
[81556] = {name = "Приказы Тьмы", description = "[q]Чо'Галл призывает Тьму себе на помощь."},
[81568] = {name = "Круговой удар", description = "[q]Вращается на месте, обрушивая шквал ударов клинками на противников, находящихся на расстоянии 5 м от заклинателя раз в 0,5 сек. в течение 4 sec. Пораженные цели получают 2500 ед. урона в секунду в течение 6 sec. Суммируется 10 раз."},
[81570] = {name = "Возвращение на берег", description = "[q]Попросите гребца вернуть вас обратно к берегу."},
[81572] = {name = "Усиленные тени", description = "[q]Чо'Галл начинает призывать освобожденные тени раз в 3 сек., нанося 34125 to 35875 ед. урона от темной магии всем противникам в радиусе 50000 м."},
[81574] = {name = "Рывок", description = "[q]Быстрый рывок к противнику."},
[81576] = {name = "Выброс лавы", description = "[q]Вы бросаете в противника раскаленную лаву, нанося 18850 to 21150 ед. урона от огня."},
[81586] = {name = "Бросок кости", description = "[q]Наносит противнику 200% обычного урона от оружия."},
[81608] = {name = "Сеть каменного трогга", description = "[q]Притягивает василиска к земле для последующего приручения."},
[81624] = {name = "Воин клана Драконьей Пасти", description = "[q]Вы ведете в бой воина из клана Драконьей Пасти."},
[81628] = {name = "Призыв оскверняющего адепта", description = "[q]Чо'Галл призывает оскверняющих адептов из ближайших порталов себе на помощь!"},
[81630] = {name = "Вязкий яд", description = "[q]Наносит 13425 to 16575 ед. урона от сил природы противнику и его союзникам, находящимся в радиусе 5 м и снижает скорость их передвижения на 30%. Дополнительно наносит 9250 to 10750 ед. урона от сил природы раз в 2 сек."},
[81632] = {name = "Бросок валуна", description = "[q]Бросить валун, наносящий 2250 to 2750 ед. урона каменным драконам врага."},
[81642] = {name = "Пыльное молотилово", description = "[q]Серия периодических ударов хвостом по земле."},
[81650] = {name = "Греховный луч", description = "[q]Поражение противника энергией темной магии, которая передается следующей цели рядом с ним. Количество целей, на которые действует заклинание – 3."},
[81651] = {name = "Ужасный луч", description = "[q]Поражение противника энергией темной магии, которая передается следующей цели рядом с ним. Количество целей, на которые действует заклинание – 3."},
[81653] = {name = "Дыбогрив", description = "[q]Естественная броня заклинателя, ощетинившегося острыми шипами, наносит атакующим физический урон."},
[81654] = {name = "Морочащий выстрел", description = "[q]Наносит урон от огня противнику и находящимся рядом с ним союзникам и дезориентирует заклинателя на 3 sec."},
[81655] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[81677] = {name = "Яростный укус", description = "[q]Наносит противнику 50% урона от оружия и заставляет его истекать кровью, нанося дополнительно 1137 to 1462 ед. урона раз в 2 сек. в течение 15 sec."},
[81678] = {name = "Выклевать глаз", description = "[q]Снижает вероятность попадания на 10% на 5 sec."},
[81680] = {name = "Визгливый вой", description = "[q]Снижение скорости передвижения находящихся рядом противников на 10% в течение 5 sec.\n Снижение силы атаки находящихся рядом противников на 10 ед."},
[81685] = {name = "Оскверняющее сокрушение", description = "[q]Поражает случайную цель, нанося 11310 to 12690 ед. урона от темной магии всем противникам в радиусе 10 м от точки попадания."},
[81690] = {name = "Запах крови", description = "[q]Наносит 1850 to 2150 ед. урона от кровотечения раз в 2 сек. Голодные кроколиски неподалеку могут учуять ваш запах! Вы для них – главная цель."},
[81691] = {name = "Лобовая атака свинобраза", description = "[q]Наносит противнику 50% урона от оружия и отбрасывает его на небольшое расстояние."},
