[81707] = {name = "Неистовство ветра", description = "[q]Шальной ветерок кружится вокруг цели, повышая скорость атаки в ближнем и дальнем бою, а также скорость произнесения заклинаний, на 10%."},
[81711] = {name = "Трещина", description = "[q]Всякий раз, когда заклинатель наносит цели урон в ближнем бою, показатель брони цели снижается на 5%. Суммируется 20 раз. Время действия – 6 sec."},
[81713] = {name = "Порочность", description = "[q]Наносит 73125 to 76875 ед. урона от темной магии всем противникам в радиусе 20 м и накладывает на них порчу."},
[81753] = {name = "Угрожающий рев", description = "[q]Повышает скорость атаки в ближнем бою заклинателя и его ближайших союзников на 10% на 30 sec."},
[81754] = {name = "Завербовать", description = "[q]Наносит противнику урон от темной магии и повышает скорость передвижения на 50%."},
[81757] = {name = "Пролитая кровь древнего бога", description = "[q]Убитый адепт древнего бога проливает порченую кровь. Наносит 21937 to 23062 ед. урона от темной магии в радиусе 10 м и накладывает эффект \"Порченая кровь\"."},
[81764] = {name = "Бросок светильника", description = "[q]Наносит противнику урон от огня и поджигает область под ним."},
[81767] = {name = "Пламя фонаря", description = "[q]Наносит урон от огня всем противникам, попавшим в огонь. Время действия – 6 sec."},
[81770] = {name = "Громокамень", description = "[q]Бросить громокамень, наносящий врагу 9250 to 10750 ед. урона."},
