[81850] = {name = "Рассекающий удар Морского Рога", description = "[q]Наносит 2775 to 3225 противнику и его ближайшим союзникам. Действует максимум на 10 целей."},
[81886] = {name = "Бросок ручных гранат", description = "[q]Взрывает воргенов-пиратов."},
[81887] = {name = "Возвращение в Пиратскую Бухту", description = "[q]Попросите виверну вернуть вас в Пиратскую Бахту."},
