[81954] = {name = "Оскверненный щит земли", description = "[q]Защита цели земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд. 10 атаки. Время действия – 5 min."},
[81995] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке и наносит дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[81996] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 6 sec."},
[82028] = {name = "Джуджу вуду", description = "[q]Левитация..."},
[82032] = {name = "Неистовство", description = "[q]Скорость атаки повышена на 8%."},
