[82107] = {name = "Глубокая заморозка", description = "[q]Цель теряет способность передвигаться."},
[82109] = {name = "Лунный луч", description = "[q]Выстрел в противника с нанесением урона от тайной магии."},
[82119] = {name = "Пылающий столб судьбы", description = "[q]Призывает столб пламени, обжигающий всех противников на выбранном участке и наносящий дополнительный урон от огня раз в 2 сек. в течение 1 sec."},
[82120] = {name = "Грохот земли", description = "[q]Нанесение физического урона находящимся поблизости противникам с оглушением их на 2 sec."},
[82133] = {name = "Огненное уничтожение", description = "[q]Атаки вызывают огненное уничтожение, которое дополнительно наносит 39000 to 41000 ед. урона от огня и вызывает заклинание \"Пламень\", поражающее ближайшую территорию."},
[82137] = {name = "Крик о помощи", description = "[q]Ром'огг призывает находящихся рядом прислужников помочь ему!"},
[82226] = {name = "Вихрь клинков", description = "[q]Начинает кружиться, вихрем налетая на всех ближайших противников и нанося им удары раз в 1 сек. в течение следующих 6 sec. Находясь под действием эффекта \"Вихрь клинков\", атакующий может передвигаться, но не может использовать любые другие способности."},
[82228] = {name = "Удар Огрского Клинка", description = nil},
[82255] = {name = "Отделение души", description = "[q]Часть вашей души будет отделена."},
[82263] = {name = "Слившиеся души", description = "[q]Души заклинателя и цели сливаются, в результате чего запас здоровья каждого падает на 41625 to 48375 ед., а наносимый урон увеличивается на 20%."},
[82285] = {name = "Стихийный стазис", description = "[q]Наполняет находящихся рядом противников энергией стихий, парализуя их и значительно уменьшая получаемый ими урон на 17 sec."},
[82294] = {name = "Огненная катапульта", description = "[q]Стреляет взрывающимися громокамнями по каменным троггам из подкрепления и грибным ужасням. Радиус действия 100 м."},
[82299] = {name = "Заражение крови", description = "[q]Вызывает заражение крови всех оскверняющих адептов, вследствие чего запекшаяся субстанция превращается в кровь древнего бога, а также заставляет закипеть кровь живых оскверняющих адептов."},
[82361] = {name = "Порча древнего бога", description = "[q]Чо'Галла полностью поражает порча древнего бога, наносящая тому 6337 to 6662 ед. урона от темной магии раз в 2 сек. Накладывает эффект \"Порченая кровь\"."},
[82362] = {name = "Теневой удар", description = "[q]Заклинатель проходит сквозь тени, появляясь за спиной враждебной цели, и наносит ей 70000 ед. урона от темной магии."},
[82376] = {name = "Ледяное сокрушение", description = "[q]Наносит 110% урона от оружия противнику и его ближайшим союзникам и снижает их скорость атаки и произнесения заклинаний на 10%."},
[82395] = {name = "Берсерк", description = "[q]Повышает скорость атак заклинателя и скорость его передвижения на 150%, а весь наносимый урон увеличивает на 500%. Дает невосприимчивость к эффектам провокации."},
[82411] = {name = "Ослабляющий луч", description = "[q]Ослабляет эффект лечащих заклинаний, наложенных на цель, и снижает ее урон на 75%, а также наносит 10725 to 11275 ед. урона от темной магии раз в 1 сек. в течение 10 sec."},
[82414] = {name = "Смутные творения", description = "[q]Чо'Галл призывает смутные творения себе на помощь!"},
[82415] = {name = "Оглушающая волна", description = "[q]Наносит 9425 to 10575 ед. урона от темной магии всем противникам в радиусе 60 м и поглощает до 15000 ед. применяемого к ним лечения."},
[82425] = {name = "Плач Тьмы", description = "[q]Наносит 26513 to 30812 ед. урона от темной магии находящимся поблизости целям, вырывая их из мира духов."},
[82506] = {name = "Пятьдесят ударов кнутом", description = "[q]Выпады в ближнем бою вызывают эффект \"Пять ударов кнутом\", наносящий обычный урон и дополнительно 5000 ед. урона."},
[82524] = {name = "Неистовство Чо'Галла", description = "[q]Чо'Галл поражает цель, нанося той значительный физический урон и урон от темной магии. Цель получает увеличенный физический урон и урон от темный магии."},
[82528] = {name = "Каменящий удар", description = "[q]Оглушение всех находящихся рядом противников в течение 6 sec."},
[82529] = {name = "Лучи из глаз", description = "[q]Наносит урон от сил природы."},
[82531] = {name = "Окутывающая тьма", description = "[q]Потоки темной магии восстанавливают тело цели, исцеляя ее раз в 3 в течение until cancelled."},
[82547] = {name = "Бросок валуна", description = nil},
