[89918] = {name = "Бубух", description = "[q]Сотрясание, наносящее урон и дестабилизирующее земную поверхность."},
[89932] = {name = "Линия разлома", description = "[q]Земля перед заклинателем вздымается, и через 2 сек. из ее недр извергаются потоки огня."},
[89942] = {name = "Защита разрушителя миров", description = "[q]Восполнение 50000 ед. здоровья раз в 5 секунд."},
[89972] = {name = "Порыв ветра", description = "[q]Наносит урон от сил природы всем противникам, находящимся перед заклинателем, и отбрасывает их назад."},
[90028] = {name = "Освобождение сущности", description = "[q]Поток сумеречной энергии, который поражает всех находящихся рядом противников, нанося им урон в количестве 10% от их максимального запаса здоровья раз в 1 сек."},
[90045] = {name = "Неукротимость", description = "[q]Усилием воли Синестра восстанавливает контроль над своими действиями, временно становясь невосприимчивой ко всем сковывающим и ограничивающим эффектам. Наносит 40000 ед. урона от темной магии противникам в радиусе 8 м и отбрасывает их назад."},
[90046] = {name = "Штормовое завихрение", description = "[q]Наносит урон от сил природы находящимся рядом противникам, отбрасывая их назад."},
[90047] = {name = "Мощь отступника", description = "[q]Наполняет цель силой Братства Справедливости. Увеличивает наносимый физический урон на 200%, уменьшает урон, получаемый от действующих на область эффектов на 90% и повышает запас здоровья на 300%."},
[90052] = {name = "Конус холода", description = "[q]Нанесение противникам перед заклинателем урона от магии льда и снижение их скорости передвижения на 8 sec."},
[90053] = {name = "Чародейский взрыв", description = "[q]Создание магической взрывной волны, наносящей противникам урон от тайной магии."},
[90054] = {name = "Деморализующий крик", description = "[q]Снижение силы атаки ближнего боя находящихся рядом противников на 20 sec."},
[90063] = {name = "Удар с размаха", description = "[q]Удар огромным бревном, наносящий 300% урона от оружия всем находящимся рядом противникам."},
[90067] = {name = "Огненное дыхание", description = "[q]Посылает во вражеские цели огненную стрелу, причиняя 13125 to 16875 ед. урона всем захватчикам из клана Черной горы."},
[90073] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 6 sec."},
[90075] = {name = "Черный раскол", description = "[q]Наносит противнику и его ближайшим союзникам увеличенный урон от оружия. Поражает вплоть до 3 целей. Противникам, пораженным этой способностью, наносится урон от темной магии раз в 3 сек. в течение 12 sec."},
[90083] = {name = "Сумеречное дыхание", description = "[q]Наносит 57600 to 62400 ед. урона от темной магии в 25-метровом конусе поражения перед заклинателем."},
[90098] = {name = "Головы с плеч", description = "[q]Наносит противнику физический урон раз в 3 сек. в течение 15 sec."},
[90099] = {name = "Слежение за противником", description = "[q]Focus on the enemy, increasing damage taken."},
[90107] = {name = "Поглощение сущности", description = "[q]Поглощение чистой сумеречной сущности.\n Увеличивает наносимый урон на 10%.\n Увеличивает запас здоровья на 10%."},
[90125] = {name = "Огненное дыхание", description = "[q]Наносит 95000 to 105000 ед. урона от огня всем противникам перед заклинателем."},
[90171] = {name = "Острые зубы", description = "[q]Нанесение противнику физического урона."},
[90208] = {name = "Нещадное месиво", description = "[q]Заклинатель наносит противнику шквал быстрых ударов в течение 5 sec сек."},
[90255] = {name = "Кольцо огня", description = "[q]Нанесение урона от огня ближайшим противникам."},
[90257] = {name = "Воспламенение", description = "[q]Поджигает противника, нанося урон от огня в течение 5 sec. и приводя его в панику. Пока цель пылает, пламя периодически обжигает находящихся рядом союзников цели, также нанося урон от огня."},
[90272] = {name = "Героический прыжок", description = "[q]Атака в прыжке, наносящая 24 to 29% урона от оружия всем противникам в радиусе 8 м от выбранной точки."},
[90309] = {name = "Ужасающий рев", description = "[q]Дьявозавр издает жуткий рев, повышающий вероятность нанесения критического удара всеми участниками группы или рейда на 5 %."},
[90314] = {name = "Переполох", description = "[q]Лисица вертит хвостом и подскакивает на месте, вздымая облако пыли. Скорость атаки в ближнем и дальнем бою всех противников в радиусе 10 м понижается на 20% на 30 sec."},
[90325] = {name = "Мощный удар", description = "[q]Сокрушительный удар, наносящий противнику урон от оружия и дополнительный урон."},
[90327] = {name = "Мертвая хватка", description = "[q]Пес смыкает зубы на противнике, удерживая его на месте в течение 4 sec."},
[90328] = {name = "Поступь духа", description = "[q]Ваш питомец становится незаметным, но скорость его передвижения снижается на 50%. Первая атака в таком состоянии наносит на 20% больше урона. Длится, пока не будет отменено."},
[90333] = {name = "Магматический выброс", description = "[q]Земля перед заклинателем вздымается, и через 2 сек. из ее недр извергаются потоки огня."},
[90337] = {name = "Грубый прием", description = "[q]Обезьяна швыряет в противника горсть чего-то очень нехорошего, ослепляя его на 4 sec. Ужасно грубо."},
[90339] = {name = "Укрепление панциря", description = "[q]Снижает получаемый урон на 50% на 12 sec."},
[90347] = {name = "Играть", description = "[q]Ваша лисица становится игривой."},
[90355] = {name = "Исступление", description = "[q]Увеличивает скорость ближнего и дальнего боя, а также скорость применения заклинаний для всех участников группы или рейда на 30%. Время действия – 40 sec.\n\n После завершения действия союзники изнемогают от усталости и не могут повторно получить положительный эффект \"Жажды крови\", \"Героизма\", \"Исступления\" или \"Искажения времени\" в течение 10 min."},
[90361] = {name = "Духовное лечение", description = "[q]Дух зверя восстанавливает выбранному союзнику [ 1238 + ((Ranged attack power * 0.35) * 0.5)] ед. здоровья и еще [ 1 + ((Ranged attack power * 0.35) * 0.335)] ед. здоровья в течение 10 sec."},
[90363] = {name = "Объятия сланцевого паука", description = "[q]Увеличивают силу, ловкость, выносливость и интеллект участников группы или рейда на 5%. Повышают сопротивление всем школам магии на [( 85 <= 70 ? 85 / 2 : ( 85 <= 80 ? 85 / 2 + ( 85 - 70) / 2 * 5 : 85 / 2 + ( 85 - 70) / 2 * 5 + ( 85 - 80) / 2 * 7)) - 0.5]."},
[90373] = {name = "Река лавы", description = nil},
[90391] = {name = "Озеро лавы", description = "[q]Наносит урон от огня ближайшим противникам."},
[90398] = {name = "Волнение моря", description = "[q]Водяная стрела последовательно поражает нескольких находящихся рядом противников, нанося им урон от магии льда. Максимальное количество целей – 3. Каждая последующая цель получает больше урона, чем предыдущая."},
[90409] = {name = "Activate Pools", description = nil},
[90446] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[90447] = {name = "Ядовитое облако", description = "[q]Создает ядовитое облако под кадавром Кальдера."},
[90522] = {name = "Водная оболочка", description = "[q]Поглощает 50000 ед. урона. Разбиваясь, водная оболочка наносит 48750 to 51250 ед. урона от магии льда противникам в радиусе 8 м."},
[90550] = {name = "Чародейский обстрел", description = "[q]Запускает в противника несколько волшебных стрел, наносящих урон от тайной магии."},
[90551] = {name = "Переполняющая энергия", description = "[q]Пускает дугу магической энергии, нанося указанному противнику урон от тайной магии."},
[90607] = {name = "Проклятие бессилия", description = "[q]Уменьшает магический урон, наносимый целью, на 10% на 15 sec."},
[90625] = {name = "Земляной шип", description = "[q]Вызов в выбранной области земляного шипа, который наносит 4675 to 6325 ед. урона от сил природы."},
[90662] = {name = "Сильный удар", description = "[q]Наносит противнику увеличенный на 50 ед. урон от оружия и отбрасывает его назад."},
[90673] = {name = "Ткань Тьмы", description = "[q]Наносит 24375 to 25625 ед. урона от темной магии противникам в конусе поражения перед заклинателем."},
[90677] = {name = "Водяная преграда", description = "[q]Создает перед заклинателем водяную преграду, поглощающую урон в размере 50% от его запаса здоровья."},
[90685] = {name = "Благословение Вечерней Зари", description = "[q]Вливает в горного олененка энергию Скверны."},
[90686] = {name = "Взрыв олененка", description = "[q]131 to 168 ед. урона всем находящимся поблизости противникам."},
[90736] = {name = "Восстанавливающее поле", description = "[q]Создает вокруг заклинателя область исцеляющих волн."},
[90764] = {name = "Языки пламени", description = "[q]Наносят 2925 to 3075 ед. урона от огня раз в 2 сек. Эффект суммируется."},
[90769] = {name = "Оживление костей", description = "[q]Швыряет кучу костей и оживляет их. Создает один комплект оживших костей раз в 3 сек. в течение 9 sec."},
[90783] = {name = "Смертельный бросок", description = "[q]Бросает кинжал в противника, нанося 24 to 29%-й урон от оружия. Кроме того, прерывает процесс произнесения заклинаний этой школы на 3 sec."},
[90789] = {name = "Жало чумы", description = "[q]Наносит противнику урон от сил природы раз в 2 сек. и увеличивает получаемый им физический урон на 10% на 8 sec."},
[90846] = {name = "Огненный шок", description = "[q]Наносит 6337 to 6662 ед. урона от огня и дополнительно 2437 to 2561 ед. урона от огня раз в 1 сек. в течение 8 sec."},
[90905] = {name = "Прыжок с рассекающим ударом", description = "[q]Прыжок к цели с нанесением 150% урона от оружия противнику и двум его ближайшим союзникам."},
[90908] = {name = "Удар в челюсть", description = "[q]Мощный удар по цели, наносящий 150% урона в ближнем бою."},
[90913] = {name = "Удар по глазам", description = "[q]Наносит физический урон."},
[90914] = {name = "Быстрина", description = "[q]Исцеляет союзника в течение 15 sec."},
[90920] = {name = "Выклевать глаз", description = "[q]Снижает вероятность попадания на 50% на 5 sec."},
[90925] = {name = "Кровавая баня", description = "[q]Жестокая резня, длящаяся 3 sec."},
[90928] = {name = "Рывок", description = "[q]Быстрый рывок к противнику."},
[90929] = {name = "Безрассудство", description = "[q]Невосприимчивость к эффектам страха.\n Все атаки наносят критический урон.\n Броня ослаблена на 35%."},
[90932] = {name = "Поле ярости", description = "[q]Создание магического щита. Находящиеся в его пределах наносят на 50% больше урона."},
[90934] = {name = "Quest Credit: Duarn", description = nil},
[90938] = {name = "Кровяная стрела", description = "[q]Наносит противнику урон и снижает скорость его передвижения на 4 sec."},
[90946] = {name = "Кровавое омовение", description = "[q]Повышает скорость атаки и скорость произнесения заклинаний союзника на 30% на 6 sec."},
[90950] = {name = "Всепоглощающее пламя", description = "[q]Наносит 40000 ед. урона от темной магии раз в 1 сек. Урон понижается, когда цель отдаляется от источника всепоглощающего пламени."},
[90951] = {name = "Коварный удар", description = "[q]Наносит 50% урон от оружия."},
[90954] = {name = "Камуфляж", description = "[q]Заклинатель сливается с окружающей средой, после чего его нельзя выбрать в качестве цели в дальнем бою."},
[90956] = {name = "Шаг сквозь тень", description = "[q]Пройти сквозь тень, чтобы появиться за спиной у противника."},
[90958] = {name = "Ускользание", description = "[q]Повышает вероятность уклонения от удара на 35%."},
[90960] = {name = "Вертящиеся клинки", description = "[q]Противника окружают вращающиеся клинки, которые наносят физический урон всем находящимся рядом целям."},
[90978] = {name = "Наполнение энергией!", description = "[q]Повышение скорости атаки и скорости произнесения заклинаний на 50% и увеличение наносимого урона на 25%."},
[90980] = {name = "Рассекающий удар", description = "[q]Нанесение в ближнем бою 150% обычного урона противнику и его ближайшим союзникам. Захватывает до 3 целей."},
[90994] = {name = "Туннель", description = "[q]Существо зарывается в землю, исчезая из виду на until cancelled."},
[91004] = {name = "Священный огонь", description = "[q]Противника охватывает пламя, обжигая и нанося дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[91009] = {name = "Мощь отступника", description = "[q]Наполняет цель силой Братства Справедливости. Увеличивает наносимый физический урон на 200%, уменьшает урон, получаемый от действующих на область эффектов на 90% и повышает запас здоровья на 300%."},
[91017] = {name = "Волна исцеления", description = "[q]Призывает силы природы для исцеления союзника."},
[91018] = {name = "Цепное исцеление", description = "[q]Наполняет раненого союзника целительной энергией, которая затем переходит на другого союзника, находящегося поблизости. Заклинание может исцелить до 3 целей, но с каждой новой целью энергия слабеет на 50%."},
[91032] = {name = "Хмельной туман", description = "[q]Увеличивает время между атаками противника на 30% и уменьшает скорость его передвижения на 50% на 10 sec."},
[91034] = {name = "Угрожающий рев", description = "[q]Повышает скорость атаки заклинателя в ближнем бою на 50% на 30 sec."},
[91036] = {name = "Завербовать", description = "[q]Наносит противнику урон от темной магии и повышает скорость передвижения на 50%."},
[91038] = {name = "Бросок", description = "[q]Бросок оружия в противника, наносящий 150% обычного урона."},
[91045] = {name = "Апперкот", description = "[q]Наносит противнику 200% урона от оружия, отбрасывая его назад."},
[91050] = {name = "Оплеуха", description = "[q]Удар по голове, наносящий 150% урона от оружия и уменьшающий вероятность уклонения или парирования атаки на 50% в течение 6 sec."},
[91053] = {name = "Бешенство", description = "[q]Увеличение скорости атаки заклинателя на 100% на 6 sec."},
[91061] = {name = "Bloody Explosion - Hands", description = nil},
[91064] = {name = "Рубящие когти", description = "[q]Наносят 75% урона от оружия текущей цели."},
[91077] = {name = "Яростный прыжок", description = "[q]Яростный упырь прыгает на жертву и атакует ее с повышенной на 50% скоростью. Время действия – 5 sec."},
[91088] = {name = "Пожирание плоти", description = "[q]Вытягивает здоровье цели в объеме 20% от максимального запаса и передает часть его заклинателю."},
[91090] = {name = "Цапнуть", description = "[q]Удар когтями, наносящий урон."},
[91099] = {name = "Гниющая желчь", description = "[q]Трупоед накапливает желчь и выплевывает ее в цель, нанося 2000 ед. урона от сил природы раз в 1 сек. и уменьшая урон и эффективность исцеляющих заклинаний на 25%."},
[91121] = {name = "Тектонический взрыв", description = "[q]Вот, что получается, когда бьешь взрывоопасную овцу."},
[91134] = {name = "Пытка разума", description = "[q]Нанесение урона от темной магии противнику и снижение скорости его передвижения на 3 sec."},
[91213] = {name = "Мощный удар", description = "[q]Наносит 19000 to 21000 ед. физического урона всем противникам в радиусе 10 м."},
[91220] = {name = "Пугающий рык", description = "[q]Вселяет ужас во всех находящихся рядом противников на 3 sec. Эффект страха рассеивается при получении любого урона."},
[91233] = {name = "Жестокий швырок", description = nil},
[91263] = {name = "Детонация ловушек", description = "[q]Приведение всех мистических ловушек в действие. Ловушки взрываются по истечении 0 сек."},
[91277] = {name = "Пылающая душа", description = "[q]Эта душа горит."},
[91289] = {name = "Костяной щит", description = "[q]Вокруг заклинателя вращается 3 кости:костей. Каждая наносящая урон атака против заклинателя уничтожает 1 кость. До тех пор, пока остается хотя бы одна кость, заклинатель получает на 20% меньше урона и наносит на 2% больше урона всеми атаками, заклинаниями и способностями. Время действия – 5 min."},
[91291] = {name = "Похищение жизни", description = "[q]Похищение 30000 ед. здоровья противника за 5 sec. и передача его заклинателю."},
[91294] = {name = "Вертящийся клинок", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому обычный урон и дополнительно 110 ед. урона. Время действия – 8 sec."},
[91297] = {name = "Призыв дракончиков", description = "[q]Призывает на помощь 3 огнечешуйчатых малышей:огнечешуйчатых малышей на 1 min."},
[91303] = {name = "Обращение в веру", description = "[q]Чо'Галл влияет на сознание цели, заставляя ее использовать заклинание \"Поклонение\". В результате на Чо'Галла накладывается эффект \"Порочная преданность\" раз в 3 сек. \"Поклонение\" может быть прервано."},
[91304] = {name = "Теневой хлыст", description = "[q]Поражает Химерона темной энергией, причиняя невероятную боль и прерывая эффект \"Вражда\"."},
[91307] = {name = "Насмешка тьмы", description = "[q]Наносит всем противникам 2000 ед. урона от темной магии раз в секунду."},
[91309] = {name = "Рывок", description = "[q]Наброситься на противника, стоящего дальше всех, нанеся ему урон, увеличенный на 150 ед., и распугав все ближайшие цели."},
[91313] = {name = "Пламенная вспышка", description = "[q]Посылает волну пламени перед големом, нанося урон от огня в секторе радиусом 10 м перед ним в течение until cancelled."},
[91314] = {name = "Толчок", description = "[q]Наносит урон и бросает противника через плечо."},
[91324] = {name = "Прикосновение тьмы", description = "[q]Руки Тьмы обхватывают цель, нанося 6000 ед. урона от темной магии раз в 3 сек. в течение 15 sec."},
[91325] = {name = "Вихрь теней", description = "[q]Создает вихрь теней, наносящий в течение 10 sec. 60000 ед. урона от темной магии всем противникам, попавшим в него."},
[91335] = {name = "Мощный удар", description = "[q]Сокрушительный удар, наносящий противнику урон от оружия и дополнительный урон."},
[91348] = {name = "Отбивная", description = "[q]Ослабляет броню противника на 3% за каждый заряд и наносит противнику 150 ед. физического урона раз в 1 сек. Суммируется до 15 раз. Время действия – 10 sec."},
[91408] = {name = "Вихрь", description = "[q]Наносит 18850 to 21150 ед. урона раз в секунду находящимся рядом противникам, но снижает скорость передвижения на 35%."},
[91415] = {name = "Бешенство", description = "[q]Скорость атаки увеличивается на 60%.\n Урон повышается на 60%."},
[91419] = {name = "Костяной шквал", description = "[q]Бросок кости наносит 9000 ед. физического урона случайному противнику раз в 1 сек. в течение 3 sec."},
[91463] = {name = "Глухая оборона", description = "[q]Снижение физического и магического урона, получаемого заклинателем, на 75% на 10 sec."},
[91522] = {name = "Темное прикосновение", description = "[q]Наносит противнику 40% обычного урона от оружия в качестве урона от темной магии."},
[91554] = {name = "Смрадное облако", description = "[q]Ядовитое облако болезни окружает тело, нанося 8000 ед. урона каждую секунду в течение 10 sec."},
[91561] = {name = "Болезнетворное облако", description = "[q]Носителя окружает болезнетворное облако, которое наносит 1950 to 2050 ед. урона от сил природы противникам в радиусе 5 м раз в 3 сек."},
[91563] = {name = "Живая бомба", description = "[q]Бросок огненной бомбы в противника. По прошествии 6 сек. бомба взрывается, нанося 20000 ед. урона от огня цели и ее ближайшим союзникам."},
[91565] = {name = "Волшебный огонь", description = "[q]Ослабляет броню цели на 4% на 5 min. Во время действия заклинания цель не может становиться незаметной или невидимой. Суммируется до 3 раз."},
[91571] = {name = "Обстрел крошшера", description = "[q]Наносит 23797 to 33202 ед. урона противникам в небольшом радиусе вокруг области воздействия. Скорость стрельбы очень высока."},
[91574] = {name = "Баллистический заряд", description = "[q]Наносит 200400 to 279600 ед. урона от огня всем противникам, находящимся на обширной территории от места взрыва. Для использования очередного баллистического заряда требуется какое-то время."},
[91594] = {name = "Wedding Phase Aura", description = nil},
[91601] = {name = "Instant Boulder Poof Spawn", description = nil},
[91611] = {name = "Удар горгульи", description = "[q]Нанесение 7400 to 8600 ед. урона от сил природы."},
[91614] = {name = "Змеи Закана", description = nil},
[91622] = {name = "Чародейский обстрел", description = "[q]Запускает несколько волшебных стрел в противника, нанося 17100 to 18900 ед. урона от тайной магии."},
[91623] = {name = "Чародейский взрыв", description = "[q]Заклинатель пускает магическую взрывную волну, наносящую 13500 to 16500 ед. урона от тайной магии ближайшим противникам."},
[91624] = {name = "Усиление магии", description = "[q]Увеличивает наносимый заклинателем урон от огня, магии льда и тайной магии на 50% на 8 sec."},
[91625] = {name = "Взрыв", description = "[q]Наносит урон от огня и сбивает с ног ближайших противников."},
[91629] = {name = "Щит из костей", description = "[q]Поглощает 100000 ед. урона. Если щит поглощает удар в полном объеме, он рассыпается, нанося 30000 ед. урона противникам в радиусе 15 м."},
[91644] = {name = "Грабеж", description = "[q]Хищная птица вырывает когтями оружие противника, разоружая его на 10 sec."},
[91646] = {name = "Посадить подсолнух", description = "[q]Создает подсолнух, который концентрирует солнечную энергию. Солнечная энергия используется для создания других растений."},
[91649] = {name = "Посадить дрызгун", description = "[q]Создает дрызгун, который стреляет в приближающихся зомби кислотой."},
[91668] = {name = "Нестабильная сила", description = "[q]Случайным образом изменяет наносимый заклинателем физический урон на 8 sec."},
[91677] = {name = "Пустулезный плевок", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 2 сек. в течение 10 sec."},
[91704] = {name = "Посадить орешень", description = "[q]Создает орешень, который оттягивает на себя атаки приближающихся вурдалаков."},
[91710] = {name = "Посадить душащую лозу", description = "[q]Создает душащую лозу, которая сжимает и постепенно убивает 1 атакующего вурдалака."},
[91723] = {name = "Удар жнеца", description = "[q]Наносит 13875 to 16125 ед. физического урона по сектору перед опытной моделью жнеца. В результате давление пара повышается на 20."},
[91726] = {name = "Рывок жнеца", description = "[q]Рывок к противнику, наносящий 27750 to 32250 ед. физического урона и оглушающий цель на 4 sec."},
[91727] = {name = "Избыточное давление", description = "[q]Вырывающийся под высоким давлением пар наносит 18500 to 21500 ед. физического урона и отбрасывает цель назад."},
[91731] = {name = "Обсидиановый сплав", description = "[q]Особый обсидиановый сплав делает опытную модель жнеца невосприимчивой к огню."},
[91737] = {name = "Возгорание!", description = "[q]Наносит урон от огня раз в 3 сек. Агрегат произведет самоуничтожение спустя 30 sec."},
[91801] = {name = "Смертельный удар", description = "[q]Наносит противнику 120% урона от оружия и оставляет на его теле серьезную рану, снижающую эффективность любых исцеляющих заклинаний на 25% на 5 sec."},
[91815] = {name = "Литой щит", description = "[q]Расплавленная сталь защищает заклинателя от магических атак и наносит урон от огня всем находящимся рядом противникам. Уменьшает получаемый магический урон на 50%."},
[91830] = {name = "Сосредоточение внимания", description = "[q]Заклинатель сосредотачивается на текущей цели, игнорируя все остальные."},
[91849] = {name = "Хватка смерти", description = "[q]Притягивает всех противников к лорду Виктору Нефарию."},
[91857] = {name = "Перегруженный генератор энергии", description = "[q]Вызов генератора энергии под случайным союзником. Генератор увеличивает урон, наносимый всеми союзниками и противниками в радиусе 5 м, на 50%. Время действия – 1 min."},
[91871] = {name = "Разряд молнии", description = "[q]Получаемый урон увеличивается на 33%.\n Наносимый урон увеличивается на 10%.\n Скорость произнесения заклинаний повышается на 10% (не суммируется с другими подобными эффектами)."},
[91933] = {name = "Устрашающий рев", description = "[q]Парализует ужасом выбранного противника на 6 sec. и заставляет остальных находящихся поблизости противников бежать в страхе."},
[91977] = {name = "Сорвавшаяся свадьба", description = "[q]Выстреливает в случайную цель стрелу. Стрела наносит урон от темной магии всем противникам в пределах 5 м от места попадания."},
[91978] = {name = "Холодные ноги", description = "[q]Заставляет противника сильно нервничать, приковывая его к месту."},
[91979] = {name = "Свадебные колокольчики", description = "[q]Зловещие свадебные колокольчики возвещают конец света. Ближайшие противники цепенеют от ужаса."},
[91980] = {name = "Взрыв бомбы", description = nil},
[91981] = {name = "Ticker Bomb Invis Removal", description = nil},
[91997] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[91998] = {name = "Бросок камня", description = "[q]Бросок камня в противника с нанесением физического урона."},
[92001] = {name = "Щит заклинателя", description = "[q]Защищает от любых видов контроля."},
[92007] = {name = "Клубящийся пар", description = "[q]Нанесение противникам перед заклинателем урона от магии льда и снижение их скорости передвижения на 2 sec."},
[92008] = {name = "Сгущающийся пар", description = "[q]Нанесение противникам перед заклинателем урона от магии льда и снижение их скорости передвижения на 2 sec."},
[92011] = {name = "Морозный пар", description = "[q]Нанесение противникам перед заклинателем урона от магии льда и снижение их скорости передвижения на 2 sec."},
[92013] = {name = "Конденсат", description = "[q]Смешивание с клубящимися испарениями. Кон-ден-са-ция."},
[92023] = {name = "Обездвиживающие тени", description = "[q]Нефариан удерживает выбранную цель, не давая ей двигаться."},
[92025] = {name = "Вскипание крови", description = "[q]Кровь всех противников в радиусе 10 м вскипает, нанося им урон от темной магии."},
[92038] = {name = "Зловредные испарения", description = "[q]Испарения сливаются и в любой момент могут развеяться, нанеся большой противникам урон и отбросив их назад."},
[92042] = {name = "Сращение", description = "[q]Наносит находящимся рядом противникам урон от магии льда и отбрасывает их назад."},
[92048] = {name = "Вливание Тьмы", description = "[q]Нефарий наполняет проводник молний темной энергией, вызывая вливание Тьмы. Вливание наносит урон от темной магии всем находящимся рядом союзникам, зависящий от расстояния до источника вливания."},
[92067] = {name = "Статическая перегрузка", description = "[q]Поражение противника электрическим ударом, который наносит 9250 to 10750 ед. урона от сил природы раз в 2 сек. в течение 10 sec. Мощные гравитационные поля могут безопасно рассеять электроэнергию."},
[92072] = {name = "Притягивающий трезубец", description = "[q]Бросает трезубец в противника и притягивает его к себе, нанося 24 to 29 ед. физического урона и провоцируя его."},
[92075] = {name = "Гравитационное ядро", description = "[q]Сжимает противника силой гравитации, нанося 10000 ед. физического урона раз в 2 сек. Пока поле действует на основную цель, оно также затрудняет передвижения и другие действия ее союзников, которые находятся рядом."},
[92100] = {name = "Ядовитая смесь", description = "[q]Мощный отвар, полностью парализующий врагов, в результате чего те не могут сдвинуться с места или ответить на атаку."},
[92118] = {name = "Зажигание", description = "[q]Пускает по земле струи пламени в разные стороны."},
[92144] = {name = "Огненный взмах", description = "[q]Наносит противнику 75% обычного урона в ближнем бою в качестве урона от огня."},
[92156] = {name = "Cancel Phase - Zone-Specific 02", description = nil},
[92157] = {name = "Посадить тыквомбу", description = "[q]Создает тыквомбу, которая взрывается, уничтожая всех находящихся поблизости зомби."},
[92177] = {name = "Армагеддон", description = "[q]Цель самоуничтожается."},
[92212] = {name = "Поражение пламенем", description = "[q]Огненный взрыв рядом с выбранным противником, наносящий 195000 to 205000 ед. урона от огня и поджигающий область воздействия."},
[92249] = {name = "Клич жизни", description = "[q]Взывает к священным силам жизни по всему нагорью. Смертокрыл обязательно обратит на это внимание… И кто знает, к чему это приведет."},
[92265] = {name = "Кристальная буря", description = "[q]Наносит 9425 to 10575 ед. урона от сил природы всем противникам раз в 0.5 сек. Время действия – 6 sec. Цель должна находиться в зоне видимости."},
[92266] = {name = "Радиационный удар", description = "[q]Нанесение противнику урона от сил природы с вероятностью его облучения."},
[92307] = {name = "Ледяная метка", description = "[q]Обозначенная цель будет преследуема ледяным шаром."},
[92380] = {name = "Дыхание ледяной бури", description = "[q]Поддерживаемая химерой атака наносит цели и всем противникам в радиусе 12 м от нее [ 206 + ((Ranged attack power * 0.40) * 0.288)] ед. урона от ледяной бури раз в 2 сек. в течение 8 sec."},
[92426] = {name = "Паралич", description = "[q]Оглушает всех противников на 8 sec. По истечении времени действия наносит 47500 to 52500 ед. урона от тайной магии. Эффект рассеивается, когда цель получает любой урон."},
[92440] = {name = "Посадить леденюку", description = "[q]Создает леденюку, которая выстреливает в приближающихся вурдалаков ледяным воздухом, уменьшая скорость их движения и атаки."},
[92548] = {name = "Вымораживание", description = "[q]Наносит находящимся рядом противникам колоссальный урон от магии льда."},
[92552] = {name = "Мгновенный взрыв", description = "[q]Наносит 175 to 225 ед. урона от огня ближайшим противникам."},
[92573] = {name = "Призыв жала природы", description = "[q]Призыв 3 сил природы на помощь заклинателю в бою на 20 sec."},
[92596] = {name = "Восполнение маны", description = "[q]Восполнение маны."},
[92603] = {name = "Конец кошмара", description = "[q]Жертва кошмара теряет рассудок вследствие пережитой психологической травмы."},
[92604] = {name = "Адреналин", description = "[q]Сердце выпрыгивает у вас из груди. Скорость передвижения повышается на 50%."},
[92608] = {name = "Удушение", description = "[q]Вы задыхаетесь и теряете 3% от максимального запаса здоровья в секунду."},
[92614] = {name = "Отражение", description = "[q]Повышает вероятность парирования на 100%, снижает вероятность попадания по вам в дальнем бою на 100% и с вероятностью 100% позволяет уклониться от заклинаний. Время действия – 10 sec."},
[92619] = {name = "Удар из тени", description = "[q]Ванесса ван Клиф уходит в тень и наносит противнику удар в спину, причиняя физический урон в течение 10 sec."},
[92622] = {name = "Смертоносные клинки", description = "[q]Ванесса быстро прыгает от цели к цели, нанося тяжелые раны."},
[92677] = {name = "Несносность", description = "[q]Гнусный бес издает слишком много шума, повышая ваш уровень звука на 10."},
[92681] = {name = "Бегство в астрал", description = "[q]Гнусный бес переходит в иной пласт мироздания, становясь невосприимчивым к магии."},
[92685] = {name = "Что за напасть!", description = "[q]Вам докучает гнусный бес!"},
[92713] = {name = "Взрыв разума", description = "[q]Нанесение противнику урона от темной магии."},
[92716] = {name = "Наделение Тьмой", description = "[q]Наделен темными способностями.\n Невосприимчив к провокациям."},
[92733] = {name = "Обстрел всякой всячиной", description = "[q]Забрасывает пространство в радиусе 150 м вокруг Психходельни всем, что под руку попадется."},
[92754] = {name = "Поглощающий мрак", description = nil},
[92789] = {name = "Взрыв кристаллического образования", description = nil},
[92796] = {name = "Львиная ярость", description = "[q]Повышает скорость атаки на 40%, при этом также наносится удар ближайшему противнику. Время действия – 12 sec."},
[92831] = {name = "Throw Black Bottle", description = nil},
[92857] = {name = "Выслеживание", description = "[q]Заклинатель переходит в режим незаметности. Продолжается до отмены заклинателем."},
[92888] = {name = "Зов сирены", description = "[q]Заставляет всех ближайших противников лишь танцевать."},
[92895] = {name = "Морская баллада", description = "[q]Пленит противника с помощью морской баллады. Наносит урон от магии льда раз в 3 сек. в течение 15 sec."},
[92915] = {name = "Вспышка огня", description = "[q]Наносит сумеречному темному дракону урон от магии огня."},
[92916] = {name = "Дар Хранительницы Жизни", description = "[q]Исцеляет заклинателя и указанного гранатового бойца в течение 6 сек. Также восполняет запас маны."},
[92917] = {name = "Slime Imbued", description = nil},
[92918] = {name = "Завершающий удар", description = "[q]Налетает на ослабленного сумеречного темного дракона, вырывая тому глотку и нанося смертельный удар."},
[92930] = {name = "Темная жижа", description = "[q]Наносит всем противникам в радиусе 3 м 15000 ед. урона от темной магии раз в 0,5 сек. Время действия – 30 sec."},
[92945] = {name = "Выпал рапирой", description = "[q]Наносит противнику 100% урона от оружия."},
[93017] = {name = "Сумеречный разлом", description = "[q]Создает разрыв, наносящий урон противникам, находящимся в иных измерениях."},
[93059] = {name = "Щит бури", description = nil},
[93091] = {name = "Молитва исцеления", description = "[q]Обращение к светлой магии для исцеления ближайших участников группы."},
[93094] = {name = "Обновление", description = "[q]Исцеление союзника раз в 3 сек. в течение 15 sec."},
[93245] = {name = "Мощь огня", description = "[q]Мощь элементаля огня убывает по мере того, как уменьшается его запас здоровья."},
[93276] = {name = "Огненные доспехи", description = "[q]Невосприимчивость к урону!"},
[93277] = {name = "Пронизывающий ветер", description = "[q]Противник обдувают пронизывающий ветер, наносящий 6000 ед. урона раз в 2 сек. в течение 30 sec. Последующие наложения усиливают эффект заклинания, однако его способно нейтрализовать стихия земли."},
[93278] = {name = "Поражение громом", description = "[q]Наносит 28350 to 31650 ед. урона от сил природы."},
[93301] = {name = "Энергия Тьмы", description = "[q]Мощь владыки теней убывает по мере того, как уменьшается его запас здоровья."},
[93306] = {name = "Улетучивание", description = "[q]Наносит 37000 to 43000 ед. урона от сил природы противнику, делая его броню призрачной и неспособной защищать своего владельца в течение 10 sec."},
[93311] = {name = "Панцирь Тени", description = "[q]Невосприимчивость к урону!"},
[93325] = {name = "Ударная волна", description = "[q]Заклинатель посылает силовую волну, которая наносит 58500 to 71500 ед. урона и оглушает всех противников, находящихся в секторе радиусом 15 м перед ним. Время действия – 3 sec."},
[93327] = {name = "Погребение", description = "[q]Противника сокрушает сила земли, наносящая 6000 ед. урона раз в 2 сек. в течение 30 sec. Последующие наложения заклинания усиливают причиняемую цели боль, однако сильный ветер способен нейтрализовать данный эффект."},
[93331] = {name = "Морозный вихрь", description = "[q]Вихри завывают, нанося 11875 to 13125 ед. урона от магии льда ближайшим противникам и снижая скорость передвижения последних на 30%."},
[93335] = {name = "Ледяной покров", description = "[q]Заклинателя окутывает сверххолодный воздух, уменьшающий получаемый урон на 50% и предотвращающий прерывание заклинаний. Поглощает тепло из окружающего мира, но сильный огонь может разрушить ледяные чары."},
[93336] = {name = "Аура возгорания", description = "[q]Заклинателя окутывает раскаленный воздух, наносящий урон всем атакующим. Для того, чтобы преодолеть этот барьер, потребуется защита от огня."},
[93362] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке и наносит дополнительный урон от огня раз в 5 сек. в течение 8 sec."},
[93373] = {name = "Шторм клинков", description = "[q]Быстро вращающиеся клинки наносят противникам, находящимся перед атакующим, 70% урона от оружия раз в 0.5 сек. в течение 4 sec."},
[93377] = {name = "Сумеречный пробой", description = "[q]Вызывает сумеречные пробои, наносящие урон ближайшим противникам, находящимся перед заклинателем."},
[93381] = {name = "Морозное прикосновение", description = "[q]Остужает нападающих."},
[93423] = {name = "Удушить", description = "[q]Барон Эшбери напускает на ближайших противников удушающие тени, наносящие урон от темной магии раз в 1."},
[93433] = {name = "Атака из-под земли", description = "[q]Червь закапывается, вызывая землетрясение, наносящее [( 206 + ((Ranged attack power * 0.40) * 0.288)) * 8] ед. урона от сил природы. Время действия – 8 sec."},
[93435] = {name = "Рев бесстрашия", description = "[q]Зверь издает рев, повышающий силу и ловкость всех участников группы или рейда в радиусе 100 м на 549 . Время действия – 1 min."},
[93449] = {name = "Бомбардировка", description = "[q]Бомбардировка всех противников на выбранном участке, наносящая урон от огня."},
[93456] = {name = "Дымовая шашка", description = "[q]Бросок на землю дымовой шашки, взрыв которой наносит урон и снижает меткость находящихся рядом персонажей."},
[93468] = {name = "Отложенная расправа", description = "[q]Барон Эшбери наконец-то сжалился над вами… в надежде причинить вам еще большие страдания! Восстанавливает всем ближайшим противникам 0% здоровья."},
[93473] = {name = "Песчаная буря", description = "[q]Вас окружают завывающие пески!"},
[93479] = {name = "Machine Gun Impact", description = "[q]Наносит колоссальный урон боевой единице противника."},
[93490] = {name = "Водоросли", description = "[q]Создает в указанной точке бассейн с водорослями, наносящими урон от сил природы и замедляющими противников."},
[93492] = {name = "Таран", description = "[q]Таранит противника, нанося при этом физический урон и отталкивая назад противников, находящихся в секторе перед заклинателем."},
[93494] = {name = "Кильватер", description = "[q]Мобус мощно бьет хвостом, нанося при этом физический урон и отталкивая назад противников, находящихся позади него."},
[93497] = {name = "Лопающиеся пузыри", description = "[q]Атакует цель множеством пузырей, наносящих той урон от магии льда."},
[93500] = {name = "Луч пузырьков", description = "[q]Выпускает пузырьки, поражая сектор перед заклинателем и нанося урон от магии льда."},
[93505] = {name = "Сотворение ледяной смеси", description = "[q]Сотворяет ледяную смесь, наносящую противникам урон от магии льда."},
[93506] = {name = "Антимагический импульс", description = "[q]Рассеивание наложенной на противника магии со снятием 1 благотворного заклинания."},
[93507] = {name = "Оковы магмы", description = "[q]Снижает скорость передвижения находящихся поблизости противников на 60% на 10 sec."},
[93508] = {name = "Обширное извержение", description = "[q]Наносит урон от огня ближайшим противникам."},
[93512] = {name = "Извержение", description = "[q]Наносит урон от огня всем ближайшим противникам, отталкивая их назад."},
[93513] = {name = "Удар пламени", description = "[q]Нанесение урона от огня противнику."},
[93514] = {name = "Расплавленная броня", description = "[q]Оплавляет броню цели, ослабляя ее на 10% в течение 30 sec."},
[93515] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 1800 to 2200 ед."},
[93520] = {name = "Пистолетный обстрел", description = "[q]Шквальный огонь, наносящий всем противникам в конусе перед заклинателем 30000 ед. урона от темной магии раз в секунду, пока они не покинут область обстрела. Время действия – 6 sec."},
[93527] = {name = "Ледяные осколки", description = "[q]Ледяные осколки летят во всех направлениях, нанося урон от магии льда и создавая обледенения, наносящие дополнительный урон от магии льда."},
[93544] = {name = "Сумеречное дыхание", description = "[q]Наносит урон от темной магии противникам, находящимся в конусе поражения впереди заклинателя."},
[93545] = {name = "Взрыв Бездны", description = "[q]Наносит урон от темной магии всем оказавшимся в темном разломе противникам по истечении 5 сек."},
[93546] = {name = "Сумеречный разлом", description = "[q]Наносит урон от темной магии всем оказавшимся в темном разломе противникам по истечении 5 сек."},
[93553] = {name = "Зона сумерек", description = "[q]Создать зону сумеречной силы. Уменьшает полученный магический урон на 75% у всех существ, находящихся в зоне."},
[93556] = {name = "Высвобожденная магия", description = "[q]Высвобождает сумеречную силу для нанесения серьезного магического урона находящимся вблизи противникам."},
[93561] = {name = "Каменная мантия", description = "[q]Окружает заклинателя каменной мантией, которая поглощает исходящий урон. Увеличивает наносимый урон в течение всего времени, пока держится мантия."},
[93562] = {name = "Создание таинственного токсина", description = "[q]Призывает солнечную сферу."},
[93568] = {name = "Плевок", description = "[q]Любимый верблюд Дормуса плюет в цель, создавая лужу, наносящую урон от сил природы противникам, находящимся в данной области."},
[93578] = {name = "Пески времени", description = "[q]Перед вами пески времени! Они уменьшают рейтинг скорости всех ближайших противников в радиусе 60 м в течение 15 sec."},
[93581] = {name = "Боль и страдание", description = "[q]Барон Эшбери причиняет цели боль и страдание, наносящие цели постепенно возрастающий урон от темной магии раз в 1 сек.!"},
[93585] = {name = "Зелье мучений", description = "[q]Наносит цели 6077 to 6922 ед. урона от сил природы раз в 2 сек, заставляя ее бежать сломя голову от невыносимой боли."},
[93587] = {name = "Ритуал кровопролития", description = "[q]Вызывает у цели сильное кровотечение, наносящее 2337 to 2662 ед. урона раз в 1 сек."},
[93589] = {name = "Дыхание болезни", description = "[q]Заклинатель поражает противника болезнетворным дыханием, нанося тому непосредственно 2850 to 3150 ед. урона от сил природы и 950 to 1050 ед. урона от сил природы раз в 2 сек. в течение 10 sec."},
[93590] = {name = "Огненное дыхание", description = "[q]Наносит урон от огня, поражая сектор перед заклинателем."},
[93592] = {name = "Армагедиллон", description = "[q]Напускает на ближайших противников геенну огненную."},
[93598] = {name = "Удар шипами", description = "[q]Бросается на противника, нанося при этом физический урон."},
[93610] = {name = "Мощная ударная волна", description = "[q]Сотрясающий землю удар, который вызывает ударную волну. Волна наносит физический урон противникам в конусе поражения перед заклинателем и сбивает их с ног."},
[93611] = {name = "Черное дыхание", description = "[q]Создает лужу черной жижи, наносящей урон от темной магии всем противникам в данной области."},
[93621] = {name = "Темный шепот", description = "[q]Разум цели заполняет темный шепот, заставляя ее следовать воле Джулак-Рока. Эффект снимается, когда уровень здоровья цели опускается ниже 90%."},
[93629] = {name = "Проклятые пули", description = "[q]Наносят случайной цели 47500 to 52500 ед. урона от темной магии. Пораженная цель также получает увеличивающийся со временем урон от темной магии в течение 15 sec."},
[93653] = {name = "Свадебный марш", description = "[q]Восстанавливает ману и здоровье находящихся рядом союзников."},
[93655] = {name = "Струя пара", description = "[q]Наносит 21 to 28 ед. урона от огня и отбрасывает противника назад."},
[93661] = {name = "Чародейский обстрел", description = "[q]Запускает в противника несколько волшебных стрел, наносящих урон от тайной магии."},
[93675] = {name = "Смертельная рана", description = "[q]Наносит противнику 1500 ед. урона в секунду и оставляет рану, снижающую эффективность любого получаемого им лечения на 10% на 8 sec."},
[93685] = {name = "Пагубный удар", description = "[q]Наносит противнику 100% урона от оружия, вырабатывая заряд нечистой силы."},
[93686] = {name = "Нечистая сила", description = "[q]Увеличивает наносимый урон на 10% и может быть израсходована на применение способностей ужасающей силы. Суммируется до 3 раз."},
[93687] = {name = "Осквернение", description = "[q]Оскверняет участок земли под ногами заклинателя. Руки оживших мертвецов появляются из-под земли и хватают все цели в этой области, замедляя их на 50%."},
[93689] = {name = "Токсичный катализатор", description = "[q]Наносит 13875 to 16125 ед. урона раз в 2 сек., но повышает вероятность нанесения критического урона на 100%."},
[93697] = {name = "Создание ядовитой смеси", description = "[q]Запускает в цель и соседних с ней противников ядовитой смесью, первоначально нанося тем 25000 ед. урона, после чего смесь впитывается в кожу и дополнительно наносит 5700 to 6300 ед. урона раз в 5700 to 6300 сек. Скорость передвижения снижена на 40%."},
[93707] = {name = "Призыв кровожадных вурдалаков", description = "[q]Вызов неподалеку одного кровожадного вурдалака раз в 0,5 сек. в течение 3 sec."},
[93708] = {name = "Взмах щупальцем", description = "[q]Отбрасывает назад всех находящихся рядом противников."},
[93711] = {name = "Чародейская стрела", description = "[q]Выстрел в противника магической стрелой, наносящей урон от тайной магии."},
[93717] = {name = "Аура Тьмы", description = "[q]Вытягивает всякую надежду с указанной области, нанося противникам урон от темной магии."},
[93720] = {name = "Нестерпимая боль", description = "[q]Барон Эшбери находит отраду, причиняя своим жертвам нестерпимую боль, наносящую 10000 ед. урона от темной магии и увеличивающую урон по цели, наносимый темной магией, на 20% в течение 2 min. Эффект суммируется."},
[93723] = {name = "Чародейский взрыв", description = "[q]Создание магической взрывной волны, наносящей противникам урон от тайной магии."},
[93738] = {name = "Расшвыривающий топот", description = "[q]Сотрясающий землю удар, который наносит физический урон противникам в радиусе 15 м и сбивает их с ног."},
[93756] = {name = "Небесный коготь", description = "[q]Бросок топором в противника. Наносит большой урон."},
[93757] = {name = "Обличие темного архангела", description = "[q]Барон Эшбери демонстрирует всю свою мощь, принимая обличие темного архангела."},
[93768] = {name = "Удар по земле", description = "[q]Мощный удар по земле, наносящий физический урон и замедляющий ближайших противников."},
[93777] = {name = "Обращение к пламени", description = "[q]Позволяет обуздать мощь стихии огня. Увеличивает наносимый урон на 50%."},
[93780] = {name = "Обращение к морозу", description = "[q]Позволяет обуздать мощь бритвенно-острого льда. Шанс критического удара увеличен на 50%."},
[93782] = {name = "Обращение к морозу", description = "[q]Позволяет обуздать мощь бритвенно-острого льда. Шанс критического удара увеличен на 50%."},
[93786] = {name = "Обращение к воздуху", description = "[q]Позволяет обуздать штормовой ветер. Скорость атаки ближнего и дальнего боя, а также скорость произнесения заклинаний повышена на 50%."},
[93787] = {name = "Обращение к воздуху", description = "[q]Позволяет обуздать штормовой ветер. Скорость атаки ближнего и дальнего боя, а также скорость произнесения заклинаний повышена на 50%."},
[93789] = {name = "Обращение к земле", description = "[q]Позволяет обуздать мощь земной тверди. Получаемый урон уменьшен на 50%."},
[93790] = {name = "Обращение к земле", description = "[q]Позволяет обуздать мощь земной тверди. Получаемый урон уменьшен на 50%."},
[93812] = {name = "Скорбь", description = "[q]Барон Эшбери демонстрирует всю свою мощь, принимая обличие темного архангела и насылая скорбь на всех ближайших противников, нанося им при этом 4875 to 5125 ед. урона от темной магии раз в 1 сек."},
[93822] = {name = "Адреналин", description = "[q]Сердце выпрыгивает у вас из груди. Скорость передвижения повышается на 50%."},
[93826] = {name = "Рассекающий удар", description = "[q]Нанесение в ближнем бою 110% обычного урона противнику и его ближайшим союзникам. Захватывает до 3 целей."},
[93829] = {name = "Вихрь", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому обычный и дополнительный урон. Время действия – 4 sec."},
[93844] = {name = "Нечистое могущество", description = "[q]Дает один заряд нечистой силы и восстанавливает здоровье в объеме 4% от максимального запаса."},
[93852] = {name = "Слово бесчестия", description = "[q]Поглощает нечестивую энергию, вызывая у противника сильнейший недуг, наносящий урон в количестве 5% от максимального запаса здоровья цели в течение 3 сек. Действует до смерти заклинателя или цели."},
[93857] = {name = "Призыв духа воргена", description = "[q]Барон Сильверлейн призывает на помощь могучий дух воргена!"},
[93861] = {name = "Цапнуть", description = "[q]Наносит противнику урон, жестоко исполосовывая его когтями."},
[93863] = {name = "Истощение души", description = "[q]Обездвиживает противника и вытягивает у того 60000 ед. здоровья в течение 4 sec., восстанавливая в два раза больше единиц здоровья заклинателя."},
[93914] = {name = "Призрачный наскок", description = "[q]Остроклык совершает прыжок к противнику, сбивая его с ног и разрывая бритвенно-острыми когтями."},
[93930] = {name = "Призрачное опустошение", description = "[q]Пригвождает противника к земле, оглушая его и разрывая его плоть. Наносит 38 to 42 ед. урона раз в 0.5 сек. в течение 3 sec."},
